INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (9103, 'zhCN', '你看到了吗?你懂它的力量了吗?净化……真是笨蛋……$B$B<麦特斯不以为意地摇了摇头。>$B$B拿去吧，离开我的视线。', 0);
