INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11778, 'zhCN', '亵渎部落的银松营火!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11779, 'zhCN', '亵渎部落的银松营火!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11780, 'zhCN', '亵渎部落的银松营火!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11781, 'zhCN', '亵渎部落的银松营火!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11782, 'zhCN', '亵渎部落的银松营火!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11783, 'zhCN', '亵渎部落的银松营火!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11784, 'zhCN', '亵渎部落的银松营火!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11785, 'zhCN', '亵渎部落的银松营火!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11786, 'zhCN', '亵渎部落的银松营火!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11787, 'zhCN', '亵渎部落的银松营火!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11788, 'zhCN', '哇，你活下来了!$B$B我猜，现在只剩下齿轮大师梅卡佐德要对付了。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11789, 'zhCN', '感谢你，$N。这个人可能会需要一段时间才能康复，但我们起码不会失去他。$B$B你说什么?你在风暴破碎者发现教徒?你最好赶快通知其它人…最好还要有证据。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11790, 'zhCN', '教徒?甲板下面?我真该把所有的船员都吊起来打!$B$B听着…为什么我们不让坎特伯理上将处理这个…情况?', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11791, 'zhCN', '多谢你的关心，$N。我们不久前才注意到这些情况。$B$B顾问泰尔伯向我保证这是个独立的意外，不需要军队的关注。$B$B我们的市政官员会确保每一个参与的人都被起诉。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11792, 'zhCN', '谨慎点，$N。我们不能在众多窥视的眼睛前查看这个证据。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11793, 'zhCN', '快点说，$C。你有什么关于教派的讯息?', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11794, 'zhCN', '你做得很好，$N。不过，我直觉这不会是我们最后一次见到这些死亡教徒。$B$B邪恶自有方法隐于日常之中。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11795, 'zhCN', '再次谢谢你，$N。有了你的帮助，就算曾经很悲惨，我或许有一天可以完成这个任务，并回到嘶轴进行报告，确实荣耀已死之人。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11796, 'zhCN', '再一次的，非常感谢你，$N。没有你，我不可能从这场灾害中取回任何有用的物品。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11797, 'zhCN', '干得好，$N。告诉这些小子你是怎么做到的。最好的蠕行者就是死掉的蠕行者!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11798, 'zhCN', '<老地精长长地吁了一口气，放松了不少。>$B$B完成了…你拯救了我们!如果你没有在这里阻止他，谁知到他的疯狂会散布得多远?$B$B在蓝龙和奥核战争的威胁之下，更别提还有巫妖王，我们最不需要的东西就是一个疯狂而且失控的泰坦制玩具!$B$B感谢你，$N!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11799, 'zhCN', '亵渎部落的银松营火!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11800, 'zhCN', '亵渎部落的银松营火!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (11801, 'zhCN', '亵渎部落的银松营火!', 0);
