INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (13772, 'zhCN', '和其它骁士彼此对战训练是最快让你明白如何击败对手的方法。只要持续练习，你绝对能在竞赛中击败他们。', 0);
