INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (24655, 'zhCN', '嘿，谢谢，孩子。拿来让我看看。$B$B<他拿着设备摆弄着>$B$B哦，情况不对啊。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (24656, 'zhCN', '你办到了，嗯？干得好，孩子。流弊。让我们看看里面是啥。$B$B<他拆开包裹，在里面乱翻>', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (24657, 'zhCN', '你拿到他的账目了？确定？太好了，$N。里面估计会有重要的信息。', 0);
