INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (2741, 'zhCN', '超级测蛋器是克格尔.曲藤的最新成果，看起来真是令人印象深刻。$B$B你刚刚接触到超级测蛋器的控制面板，它就劈啪作响了起来。', 0);
