INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (2766, 'zhCN', '发现目标!你赫然看到石头上躺着一只……机器小鸡?!从机器小鸡中传出了声音:$B$B“太好了，你干得不错!这确实是我的母体，根据我的检测判断，它需要进行一定的启动动作才能够重新飞回藏宝海湾进行修理。请把定向器放到母体机器人里，剩下的它自己会去搞定!”', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (2767, 'zhCN', '没错，我就是奥格索普.奥布诺提斯，世上最强的发明家，随时为您效劳!现在，我能帮您什么忙吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (2768, 'zhCN', '你找到布莱中士了吗?找到我的探水棒了吗?', 0);
