INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (4343, 'zhCN', '你找到了一株病怏怏的、被腐化了的迎风花。这株植物上面挂着的果实看起来已经腐烂，而且含有剧毒。你得想办法让它恢复到正常状态。', 0);
