INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (4362, 'zhCN', '我……我在哪儿?你是谁?$B$B<铜须公主看着达格兰.索瑞森大帝的尸体。>$B$B你对我亲爱的达格兰做了什么?!', 0);
