INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (4382, 'zhCN', '你可以把绿色能量水晶和蓝色能量水晶组合在一起。$B$B根据科琳给你的说明书上的记载，这种组合所制造的水晶可以提高你的精神属性。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (4383, 'zhCN', '你可以把绿色能量水晶和红色能量水晶组合在一起。$B$B根据科琳给你的说明书上的记载，这种组合所制造的水晶可以提高你抵抗物理攻击的能力。', 0);
