INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (4450, 'zhCN', '什么什么?我能为你做什么吗，$C?我非常忙，有很多人排队等着跟我做买卖。也许你可以长话短说让我不至于浪费太多时间，我并不是想要赶你走，我只是没有时间来完成我手头上的这一大堆工作。梦想太多，时间太少!', 0);
