INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (5892, 'zhCN', '你拿到补给品了吗，$N?我们的补给品一直都很缺乏!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (5893, 'zhCN', '你去过冷齿矿坑了吗，$N?你拿到我要的补给品没有?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (5901, 'zhCN', '你拿到白蚁了吗，$N?我确定像你这样聪明的$C肯定可以很轻松地完成这个任务。$B$B一旦你收集到了足够的白蚁，我们就能确保再也没有人可以使用那个伐木场了!', 0);
