INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6073, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6074, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6075, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6076, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6081, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6082, 'zhCN', '你驯服蝎子了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6083, 'zhCN', '你需要练习以掌握有效控制宠物的方法。你驯服海浪蟹了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6084, 'zhCN', '你认为雪豹怎么样，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6085, 'zhCN', '你驯服冰爪熊了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6086, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6087, 'zhCN', '你找到草原捕食者了没有?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6088, 'zhCN', '你很快就会准备就绪，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6089, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6101, 'zhCN', '了解自然的平衡是每个夜精灵的必修课。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6102, 'zhCN', '我为你的成功而自豪，$N。你完成我给你的任务了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6103, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6121, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6122, 'zhCN', '你是月光林地的塞纳里奥议会派来的?太好了!那些中毒的鹿危在旦夕，我们很高兴能够获得你的帮助。$B$B你拿到德迪利特.星焰所说的水样了吗?他告诉过我说你会拿来一份水样，让我们一起找出应对的方法。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6123, 'zhCN', '你拿到我制作解药所需的材料了吗?鹿群无法自己恢复健康，所以我们一定要帮助它们!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6124, 'zhCN', '你完成了奥伯丁的奥兰达利亚.夜歌给你的任务了吗?不要倦怠!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6125, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6126, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6127, 'zhCN', '你是月光林地的塞纳里奥议会派来的?太好了!我们正在致力于救治那些中毒的蹬羚，很高兴能够获得你的帮助。$B$B你拿到德迪利特.星焰所说的水样了吗?他告诉过我说你会拿来一份水样，让我们一起找出应对的方法。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6128, 'zhCN', '你拿到我制作解药所需的材料了吗?瞪羚无法自己恢复健康，所以我们一定要帮助它们!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6129, 'zhCN', '你完成了十字路口的图加.符文图腾给你的任务了吗?不要倦怠!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6130, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6131, 'zhCN', '继续努力，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6132, 'zhCN', '你有没有看到我妹妹梅丽萨?她总是到处乱跑，到处给我惹麻烦。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6133, 'zhCN', '不要让我空等，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6134, 'zhCN', '我的鬼魂电浆在哪里，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6135, 'zhCN', '我不建议你独自迎接这次挑战，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6136, 'zhCN', '最近我有没有告诉你，我恨你?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6141, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6142, 'zhCN', '我等不及要带些新鲜的软壳蚌肉去钓鱼了，你弄到我要的十块肉了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6143, 'zhCN', '我还能听到我的手下在被纳迦屠杀时哭喊求救的声音。如果不是因为我的膝盖坏掉了，我肯定会跟你一起去，帮你杀死那些混蛋纳迦!$B$B你通常都可以在水下的珊瑚礁深处找到他们……潜伏着……策划着阴谋……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6144, 'zhCN', '你就是纳萨诺斯最能干的特工?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6145, 'zhCN', '如果你成功了，那你也许可以就此赢得我的尊敬，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6146, 'zhCN', '这是不可接受的，士兵!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6147, 'zhCN', '什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6148, 'zhCN', '怎么样了，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6161, 'zhCN', '这个箱子被牢牢地锁着。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6162, 'zhCN', '你拿到那野兽的爪子了吗，$N?它会为它的罪行付出代价的。我不管它是堕落了还是仅仅因为生活在这片阴暗的森林中而感到愤怒。任何熊怪都不能在对我的家庭犯下这种恶行之后逍遥法外!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6163, 'zhCN', '在希尔瓦娜斯让我获得自由之后的这几个月来，我一直都在这片废土上狩猎那个混蛋，它居然躲进了斯坦索姆……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6164, 'zhCN', '你找到我的收据册了吗?我还等着重新开业哪!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6181, 'zhCN', '你看起来是要急着赶路。嗯，你来对地方了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6182, 'zhCN', '你是乌瑞恩派来的?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6183, 'zhCN', '你做出决定了吗，$N?你愿意在此时此刻再次挺身而出，为联盟效力吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6184, 'zhCN', '你来得正好。很高兴见到你，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6185, 'zhCN', '你发现……发现什么了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6186, 'zhCN', '你这么快就回来了，$N。你从瘟疫之地带来了什么消息?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6187, 'zhCN', '<瓦里安.乌瑞恩国王陷入了沉思。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6221, 'zhCN', '一旦你获得了木喉部族的信任，作为对你的忠诚的回报，也许我们可以帮你做点什么。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6241, 'zhCN', '我们很感激你为木喉部族所做的一切，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6261, 'zhCN', '看你满头大汗的，$g小伙子:小姑娘;，跑来跑去的真是太辛苦了。下一次，你应该搭乘狮鹫兽!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6281, 'zhCN', '你是在到处旅行吗?有没有去过什么有趣的地方?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6282, 'zhCN', '不要对焦炭谷掉以轻心，许多邪恶的生物都盘踞在那里。保持斗志，$N，你将帮助我们消除这些威胁。现在出发去痛击敌人吧―让我们的后代永远都不知道这些邪恶曾经存在过。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6283, 'zhCN', '血怒撕裂者是个肮脏的生物!你可以在焦炭谷西部的山岭中找到她。$B$B只要干掉了她，血怒鹰身人就会乱作一团……然后我们就可以把它们完全消灭掉!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6284, 'zhCN', '贝瑟莱斯是个残忍的猎食者……我晚上都不敢独自在蛛网小径行走。每当那个时候，她和她的孩子们就会潜伏在黑暗中，准备猎捕无辜的旅人。我也曾经落入她的魔爪，幸运的是我活着逃了出来。我会为干掉这头怪兽而付出一切的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6285, 'zhCN', '你从暴风城回来了?奥斯瑞克的护甲拿到了没有?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6301, 'zhCN', '战争带来的持续破坏，还有那些想要从木材贸易中牟利的商人都深深地刺痛了我。我们所能做的就是尽量治愈大自然，种植盖亚之种，用新生的森林来抚平大地的创伤。你弄到我要的盖亚之种了吗，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6321, 'zhCN', '你似乎是有公务在身……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6322, 'zhCN', '如果你非得进行长途跋涉的话，最好选择乘坐蝙蝠的方式。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6323, 'zhCN', '我的武器保养得无懈可击。它们毫无瑕疵，随时都可使用。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6324, 'zhCN', '$N，你回来了。你从幽暗城带来我们所需的物资了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6341, 'zhCN', '我能帮你什么忙吗?你是来搭乘角鹰兽的吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6342, 'zhCN', '你是从泰达希尔来的?告诉我，那里的渔业近况如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6343, 'zhCN', '$N，你从奥伯丁必来了?你和赖尔德谈过了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6344, 'zhCN', 'How can I help you?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6361, 'zhCN', '你是来租用双足飞龙的吗?你需要运送什么货物吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6362, 'zhCN', '你肩膀上有来自贫瘠之地的灰尘。你同我的朋友加翰见过面吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6363, 'zhCN', '你好，$C。我能为你做什么吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6364, 'zhCN', '$N，你是从雷霆崖回来的吗?你把皮革送到安哈努那里了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6365, 'zhCN', '你需要快速赶到某个地方去?找我就对了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6381, 'zhCN', '你所种下的种子将使得森林更加强大，使它可以抵御那些足够杀死所有新生命的堕落能量。它们将不断生长，防止死亡和破坏继续在这块土地上蔓延。$B$B你种下所有的盖亚之种了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6382, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6383, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6384, 'zhCN', '这味儿真好闻!你身上带着新鲜的肉，对吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6385, 'zhCN', '你需要租用双足飞龙吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6386, 'zhCN', '你是从奥格瑞玛回来的吗?格雷什卡喜欢我送给她的肉吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6387, 'zhCN', '你这么快就办完了在塞尔萨玛的事儿吗?现在你是要转至其它座城市吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6388, 'zhCN', '似乎你是要外出旅行，需要乘坐狮鹫兽吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6389, 'zhCN', '你完成任务了吗，$N?你把白蚁放出来了吗?愿圣光保佑我们，将血色十字军逐出伐木场和壁炉谷，我们不能让他们控制任何一处地方。血色十字军只不过是利用上一次战争的牺牲者来达到自己疯狂的目标而已，因此你应该像杀死那些发疯的动物一样杀死他们!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6390, 'zhCN', '你完成任务了吗，$N?你把伐木场里的白蚁放出来了吗?我的系列成员失去了工作和生命，这糟糕透了，但是看着我儿时成长的地方被那些令人厌恶的所谓圣徒占据着……啊!!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6391, 'zhCN', '$g先生:女士;，我能为您做点什么吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6392, 'zhCN', '啊，$N。你已经从铁炉堡回来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6393, 'zhCN', '海潮议会开始变得有些不耐烦了。他们试图结束这场持续数年、波及各个位面的战争。我的同胞们迫切地希望听到战胜那些讨厌的火元素的消息。你拿到我要的东西了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6394, 'zhCN', '你进入燃刃集会所了吗，$N?你找到我的镐了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6395, 'zhCN', '我们尊重死者，$N。这是我们与天灾军团的不同之处……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6401, 'zhCN', '你把这个好消息告诉塔姆拉.荒原了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6402, 'zhCN', '我就知道你会来的，$N!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6403, 'zhCN', '我……我……我没有力量了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6421, 'zhCN', '你在滚岩峡谷有什么发现吗?我记得一些与共鸣水晶有关的传说……但是我没法亲自去那儿。或许当我知道洞穴底部都有些什么东西的时候就能洞察他们的意图了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6441, 'zhCN', '我希望你能快点找到这些角，$N。我马上就要回棘齿城了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6442, 'zhCN', '纳迦的数量正在激增，$N。请赶快完成我交给你的任务。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6461, 'zhCN', '嘿，你杀死那些浑身长毛、性格暴躁的畜生了吗?我在石爪山什么都不怕，但是晚上我可不敢在附近闲逛……你该明白我的意思吧!$B$B只有我确信自己不会成为它们的盘中餐，我才会感到安全。$B$B祝你好运，$C!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6462, 'zhCN', '你找到符咒了吗，$N?一想到那些神圣的符咒落在熊怪手中，我就气不打一处来!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6481, 'zhCN', '如果我们不能尽快解决威胁的话，也许我们会丧失整个石爪山地区。任何魔法都无法与土灵的力量抗衡。当高戈罗克清醒时，他的力量将因长期沉睡而减弱，你必须利用这个机会……去吧，$N!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6482, 'zhCN', '你看见我的兄弟卢尔了吗?他几天前进入了森林，一直都没有回来……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6501, 'zhCN', '我感到了你身上散发出的邪恶气息，$r。是什么让你被这种气息笼罩?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6502, 'zhCN', '时间的流动不会停止或滞后，$r。我的生命是永恒的，但是你的却不是。不要浪费时间了……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6503, 'zhCN', '我等待着你杀死先驱者的消息，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6504, 'zhCN', '我希望你尽快把纸张找回来，$N!我必须马上回去工作!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6521, 'zhCN', '你找到大使了吗，$N?他死了吗?$B$B我们承受不起让天灾军团在卡利姆多建立据点的威胁。现在我们陷入了两面作战的境地，这足以说明他们已经对我们构成了足够的威胁。$B$B你告诉我的事情中唯一让我感到乐观的事情就是那个野猪人的首领看起来还未被腐蚀。她肯定知道天灾军团可能会带来巨大的力量，但如果她同意接受他们的条件，贫瘠之地并不会真正成为她的地盘。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6522, 'zhCN', '你有什么事，$N?如果你不断来烦我，那你一定是有什么你以为很重要的事情。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6523, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6541, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6542, 'zhCN', '又一个精神抖擞的新兵，不错!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6543, 'zhCN', '快点，$N。我们必须拿到他们的报告，事关紧急。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6544, 'zhCN', '托雷克被派去执行突袭银翼哨站的任务了，他取得胜利了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6545, 'zhCN', '那么，你是来给我传达上面的命令的?看见你这样体格健壮、意志坚定的新兵，我总是会感到很高兴。$B$B要是你希望继续在这里待下去的话，最好尽快成长起来。纳迦的威胁正在日益增长，$N。自从我到这儿之后，就开始着手研究对策，并在这个前哨站里抵挡了他们的多次进攻。$B$B但是如果你也想要投入到对敌作战中，就去跟这里的其它人也谈谈吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6546, 'zhCN', '这条路上危机四伏，$N。你最好在整个旅途中都牢记这一点。联盟的威胁近在咫尺。$B$B我们已经陷入了四面受敌的困境，灰谷东部的萨特是一种可怕的生物，而纳迦也已经占据了艾萨拉的大片区域。$B$B我们的军队数量始终维持在较高的水准，这很好，随时准备应付我们的敌人吧，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6547, 'zhCN', '保持警惕，$r。精灵总是潜伏在黑暗中……$B$B我们必须继续监视敌人。我们在努力地控制这片土地，而精灵也在寻找机会从我们手中夺走它。$B$B我想你是来传达卡德拉克下达给我的新命令的吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6548, 'zhCN', '你杀死他们了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6561, 'zhCN', '愿大地之母与你同在，$N。$B$B世界和平是我们的目标。大地之母对整个世界有一个宏伟的计划，我们都在其中扮演了某种角色。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6562, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6563, 'zhCN', '搜寻水晶的工作进行得如何，$N?你有见到纳迦在采集它们吗?祖先保佑，希望我们能找到纳迦收集这些东西的原因。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6564, 'zhCN', '什么事，$N?你需要什么吗?对于纳迦正在收集的那些水晶，我还知之甚少。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6565, 'zhCN', '你找到他了没有，$N?我们必须阻止洛古斯!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6566, 'zhCN', '耐心点，$N。注意听!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6567, 'zhCN', '幸会，$r。我是罗卡洛。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6568, 'zhCN', '我很少在这里见到部落来的$r。你给米兰达带什么来了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6569, 'zhCN', '那些眼睛是制作幻影所必需的重要材料，$N。$B$B<米兰达指了指一个标着“血色十字军”字样的满是眼球的袋子，哈哈大笑着。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6570, 'zhCN', '你是来接受考验的吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6571, 'zhCN', '希望你能成功运送这批物资!如果我不快点运送这些货物我就会有大麻烦了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6581, 'zhCN', '战歌锯刃!当然，我这儿就有。$B$B……但是你要如何回报我呢?要知道，你可不能白拿!我的时间可不能白费!$B$B我真正想要的是一把好枪，我要在旅途中随身携带它。女孩子要学会自我保护，你明白吗?我想，精通工程学的人可以毫不费力地制作出一把我喜欢的枪来。$B$B如果你能给我一把威力强大的火枪，我就给你这些锯刃。这笔交易如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6582, 'zhCN', '<艾博斯塔夫发出嘶嘶声。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6583, 'zhCN', '在你浸浴敌人的鲜血前不要回来，让它们感受死亡和痛苦吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6584, 'zhCN', '小家伙，你必须知道，时间紧迫……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6585, 'zhCN', '改变……我感觉到了你的改变，小家伙。通过这个试炼，等着你的就是庆典了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6601, 'zhCN', '别慌, $N！', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6602, 'zhCN', '达基萨斯并不是那么好对付的，$N。死在他手上的人不计其数。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6603, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6604, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6605, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6606, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6607, 'zhCN', '有东西上钩了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6608, 'zhCN', '我不太常有访客……让我算算上次有人来找我是那一年的事情了……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6609, 'zhCN', '我猜你是老矮人的朋友，……是不是?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6610, 'zhCN', '我真是迫不及待了!在我把自己的存货都吃完之前，你得把这事给办妥了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6611, 'zhCN', '<戴格正在尝试吃下一只皮鞋。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6612, 'zhCN', '<戴格正在尝试吃下一只皮鞋。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6621, 'zhCN', '你击败了穆戈特酋长了吗，$N?你拿回他的图腾了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6622, 'zhCN', '干得太出色了，医生。实在是太出色了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6623, 'zhCN', '你一定是新来的吧。很高兴你来到这里，医生。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6624, 'zhCN', '干得太出色了，医生。实在是太出色了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6625, 'zhCN', '你一定是新来的吧。很高兴你来到这里，医生。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6626, 'zhCN', '狩猎行动进行得怎么样了，$N?看到这么一小批家伙，我就已经不再怀疑天灾军团是这个世界上最大的威胁了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6627, 'zhCN', '生命中充满了疑问，$C。你永远无法全部回答它们，但是随着时间的流逝和学习的深入，也许你可以获得更多的知识。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6628, 'zhCN', '我觉得，如果你想要在这个世界上生存的话，就需要明白一个道理:你知道的越多，你能看到的就越多。$B$B如果你不断学习，不断观察，那么你就可以看到这个世界上的许多其它$r看不见的东西。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6629, 'zhCN', '你把格鲁迪格.暗云和他的手下都干掉了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6641, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6642, 'zhCN', '黑铁矿蕴藏在黑石山脉的深处，对于兄弟会来说，它是很有价值的商品。拉格纳罗斯神圣的护甲和装备都需要黑铁。$B$B你每给我十块黑铁矿石，兄弟会对你的好感就会增加。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6643, 'zhCN', '在熔火之心中人人命运难测，死亡说到就到，惩罚来得毫不留情。$B$B兄弟会里面有很多人为了搜寻炽热之核而丧失生命。$B$B给我一个炽热之核，兄弟会对你的好感就会增加。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6644, 'zhCN', '你和瑟银兄弟会同心同德，$r。因此洛克索斯才会被授权与你进行交易。$B$B拿一块熔岩之核来，你就会得到兄弟会的好感。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6645, 'zhCN', '许多神圣的配方需要拉格纳罗斯心爱的宠物皮毛。$B$B拿二块来，将可以获得兄弟会对你的好感。从熔火之心的熔核犬身上割下的熔核犬皮。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6646, 'zhCN', '火焰之王的眼泪是一种非常稀有的珍宝。$B$B给我一个山脉之血，你就可以获得兄弟会的好感。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6661, 'zhCN', '呀……$g哥们:小姐;，你还没搞定吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6662, 'zhCN', '<尼普希把他的食指伸进纸箱的换气孔。>$B$B生气勃勃，活力十足……而且时间刚刚好!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6681, 'zhCN', '你遇到密尔顿了吗?$B$B<法拉德笑了起来。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6701, 'zhCN', '我们与辛迪加交战数年，他们战斗技艺极差，却屡屡以数量取胜。单凭砍杀是不可能对他们的数量造成明显影响的!$B$B不过我们发现，要削减辛迪加的人数，最好的办法是窃取他们的纹章。谁丢失了纹章，他们的组织就会“处理”谁。$B$B去翻他们的口袋，把纹章交给我或是其它的守卫。$B$B你就从南边的敦霍尔德城堡开始吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6721, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6722, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6741, 'zhCN', '当你和敌人作战的时候，别忘了帮我收集他们的护甲碎块。我会用这些碎块来给我们自己的军队制造护甲!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6761, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6762, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6781, 'zhCN', '奥特兰克山谷中的战斗永不停息，从铁炉堡送来的补给并不能满足我们军队的需要。$B$B如果你可以从战场上收集护甲碎块的话，那就把它们交给我吧!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6801, 'zhCN', '我来自雷霆崖，$C。凯恩亲自指示我在眼前这个艰难的时刻协助霜狼氏族。$B$B我们简单明了地说吧，你必须击倒我们的敌人，把他们的鲜血交给我。一旦我收集到了足够的鲜血，召唤仪式就可以开始了。$B$B当元素之王冲入矮人的部队中时，胜利就是我们的了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6804, 'zhCN', '你拿到那些护腕了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6805, 'zhCN', '胆怯不是我们所尊崇的品德，$C。去希利苏斯证明自己的勇敢吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6821, 'zhCN', '你拿到眼珠了吗，$C?你有能力杀入黑石塔吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6822, 'zhCN', '胜利在熔火之心向你招手，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6823, 'zhCN', '你的战役还没有结束，$N。去打败我们的敌人，空气、大地和火焰的奴仆，这样也许你会在将来的某一天赢得水元素的尊敬。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6824, 'zhCN', '你打败鲁西弗隆和其它的家伙了吗，$N?你拿到他们的手了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6825, 'zhCN', '我们必须准备新的空军部队，$C!我们的驭风者已经准备就绪了，但是首先，我必须让它们吃饱喝足，这样它们才能做好突击的准备!$B$B雷矛士兵总会随身带着一些肉，从他们那里把肉抢过来，让我们的双足飞龙吃饱!我相信你可以办成这件事的，对不对?出发吧!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6826, 'zhCN', '你工作得很努力，$N，但是我们只是刚刚开始而已!$B$B我的驭风者部队要在吃饱之后才能对敌人发起突袭!$B$B我的部队在我们的空军中实力排名第二，它们将对敌人最强的部队发起进攻。因此它们也需要优质的食物，从雷矛士官那里抢夺他们随身携带的生肉吧。$B$B快点出发，勇士!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6827, 'zhCN', '我曾经被终日锁在一个矮小的洞穴里，我要报仇!$B$B我们必须仔细计划一下。$B$B首先，我的驭风者必须吃到最优质的食物，才能储备精力以待发起攻击。从雷矛指挥官那里抢一些生肉来喂养它们。不幸的是，那些可恶的家伙都躲在最后方!显然，你面临的是一个艰巨的任务。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6844, 'zhCN', '嘿，你好!嗯……$B$B老实对你说，朋友……你的体味有些夸张。也许有人说你浑身都散发着恶臭。我不会对你的种族进行抨击，我只是说说……下一次你离开战场的时候最好清洗一下，这会使你给其它人留下的第一印象大大改善!$B$B总之……我能为你做什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6845, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6846, 'zhCN', '我的军队已经整装待发，准备攻击。只要诺雷格.雷矛中尉一声令下，我们就会发起冲锋!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6847, 'zhCN', '全视之眼，你这个蠢货。你拿到全视之眼了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6848, 'zhCN', '全视之眼，你这个蠢货。你拿到全视之眼了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6861, 'zhCN', '“像山那么大的一堆”是个哥布尔工程学的术语……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6862, 'zhCN', '“像山那么大的一堆”是个哥布尔工程学的术语……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6881, 'zhCN', '霜狼氏族被一种堕落的元素能量保护着。如果我们不加以注意的话，他们的萨满必然会利用这种力量来毁掉我们。$B$B这种能量已经不是议会可以控制的了!我们必须找到伊弗斯，寻求他的帮助。$B$B霜狼氏族的战士身上带着一种名叫暴风水晶的符咒，我们可以用这些符咒来召唤伊弗斯。快去拿来那些水晶吧，$N!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6901, 'zhCN', '我的战士渴望战斗!我所需要的就是进攻命令……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6921, 'zhCN', '你找到深渊之核了吗?没有它的话，我们就无法得知暮光之锤究竟在那里做什么。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6922, 'zhCN', '啊，又见面了，$N。我正在像往常一样观察海洋的波涛。什么风把你吹到这里来了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6941, 'zhCN', '你必须去对付守卫前线的部落精英士兵!我现在命令你去削弱那些绿皮蛮子的力量，把他们的中尉和军团士兵的勋章给我拿来。当我拿到足够的勋章时，我会命令开始对他们进行空中打击的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6942, 'zhCN', '我的狮鹫兽应该在前线作战，但是在那里的敌人被削弱之前，它们是无法发动攻击的。$B$B部落的战士胸前挂着代表荣誉的勋章勇猛冲锋，而你要做的就是从他们腐烂的尸体上把勋章拿下来，并把它们交给我。$B$B只要敌人在前线的力量受到足够的打击，我就会发出命令进行空袭!我们将从空中给敌人造成致命的创伤!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6943, 'zhCN', '它们的士气很低，战士。自从我们上次对部落的空中打击失败之后，它们就拒绝再次飞行!你必须鼓舞它们的士气。$B$B回到战场并攻击部落的核心力量，杀死他们的指挥官和卫兵。尽可能带回更多的勋章!我向你保证，当我的狮鹫兽看到这些战利品并嗅到敌人的鲜血时，它们就会再次起飞!现在就出发吧!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6961, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6962, 'zhCN', '一天到晚给别人礼物让我自己也想要礼物了。嗯……我要姜饼和牛奶……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6963, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6964, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6981, 'zhCN', '你好啊，风尘仆仆的$r。我可以为你做些什么吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6982, 'zhCN', '你去过部落领地中的冷齿矿坑了吗，$N?你有没有抢到他们的补给品?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6983, 'zhCN', '$N―我们冬幕节的礼物如果没有那些货物的话就准备不好了!格拉库就没有机会和这些好孩子们一起分享他的美味肉派了……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6984, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (6985, 'zhCN', '你去过深铁矿坑了吗，$N?你有没有拿到那里的补给品?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7001, 'zhCN', '没有你的帮助，霜狼氏族的狼骑兵就无法获得足够的坐骑来进行作战。你干得很好!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7002, 'zhCN', '在我们的一些战士帮助兽栏管理员捕捉巨狼的同时，其它人还需要去找一些将它们变成坐骑所必需的东西，比如缰绳和坐鞍。$B$B你必须去猎捕这个地区的山羊，也就是那些与雷矛骑兵的坐骑很相似的山羊!$B$B杀了它们，把它们的皮交给我。只要收集到足够多的羊皮，我们就可以开始为骑手们制作坐具了。霜狼部族的狼骑兵很快就将再次驰骋在战场上!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7003, 'zhCN', '微缩残渣，我可以确信地告诉你，它是工程学中非常重要的一种材料!好吧，它可以确保压缩器不会发生诸如爆炸之类的让人感到不爽的故障。$B$B哦，我说的当然不是给你的那一台。当然不是!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7021, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7022, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7023, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7024, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7025, 'zhCN', '所有这些关于礼物的谈话都让我十分向往。嗯~~~~~姜饼与牛奶…', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7026, 'zhCN', '没有鞍具，雷矛部族就无法重新投入到战斗中!我们必须为坐骑配上鞍具，这样我们才不会像野蛮人一样骑着一头光溜溜的山羊。$B$B我们可以简单地干掉基地周围的山羊来获得皮革，但是那样做是非常愚蠢的。因为我们还要把那些山羊驯化成坐骑哪!$B$B你必须去攻击敌人的基地，杀死他们的霜狼坐骑，然后拿回他们的皮。把它们的皮交给我，这样我就可以帮我们的骑兵制作鞍具了。出发吧!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7027, 'zhCN', '没有你的帮助，雷矛部族的山羊骑兵就无法获得足够的坐骑来进行作战。你干得很好!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7028, 'zhCN', '不要问问题!$B$B我给你的指示是非常明确的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7029, 'zhCN', '如果你可以治疗那些植物，它们就可以再次生长了。我们必须尽力而为，$N……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7041, 'zhCN', '如果你可以治疗那些植物，它们就可以再次生长了。我们必须尽力而为，$N……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7042, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7043, 'zhCN', '$N―我们冬幕节的礼物如果没有那些货物的话就准备不好了!格拉库就没有机会和这些好孩子们一起分享他的美味肉派了……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7044, 'zhCN', '我从看似永无止境的梦魇中醒来……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7045, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7046, 'zhCN', '如果你希望重新启动仪式，请放弃我们的这一次尝试，然后再和我谈谈。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7061, 'zhCN', '我敢打赌莫高雷以及谦卑的雷霆崖都非常欢迎你，朋友。我对选择去关注王国，人民以及其传说的人一直都非常的感兴趣，这就是你站在我面前的原因吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7062, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7063, 'zhCN', '我敢打赌铁炉堡王国很欢迎你，朋友。我对选择去关注王国，人民与及其传说的人一直都非常的感兴趣，这是你站在我面前的原因吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7064, 'zhCN', '我原本想要自己去进行这场战斗，但是我们没有选择―我们需要更加强大的帮手，而艾泽拉斯的各个种族早已证明，只要他们团结起来就可以击败非常可怕的敌人。所以我寻求你的帮助。$B$B我希望这已经足够了。$B$B作为雷姆洛斯的兄弟，札尔塔给这个世界带来了无尽的痛苦，并最终导致了他自己的死亡。我只希望我们不会再造成更多的死亡。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7065, 'zhCN', '我原本想要自己去进行这场战斗，但是我们没有选择―我们需要更加强大的帮手，而艾泽拉斯的各个种族早已证明，只要他们团结起来就可以击败非常可怕的敌人。所以我寻求你的帮助。$B$B我希望这已经足够了。$B$B作为雷姆洛斯的兄弟，札尔塔给这个世界带来了无尽的痛苦，并最终导致了他自己的死亡。这对我们来说是很有报警意义的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7066, 'zhCN', '你身上有堕落的气味，$N……最近一段时间到这里来的许多旅行者都是这样。但是你身上的有所不同。你来这里是有什么事情要告诉我的吗?你身上有一种让我感觉非常熟悉的气息，但是我不知道那是什么。$B$B请你告诉我，你为什么要来找我?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7067, 'zhCN', '你考虑得怎么样了，$N?你能帮助我吗?你愿意进入那块圣地并帮我重建一个伟大的半人马部族吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7068, 'zhCN', '你找到水晶了吗?强大的仪式，惊人的法术―那些裂影碎片可能对于各种魔法都有极大的帮助。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7070, 'zhCN', '你去过玛拉顿了吗?我知道那是很危险的，但是如果你成功了，那一切努力都是值得的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7081, 'zhCN', '$N，你还没有占领一座墓地。当你完成任务之后再回到我这里来!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7082, 'zhCN', '为联盟占领一座墓地!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7101, 'zhCN', '你还没有攻陷任何一座哨塔，$N!你是不是在等着别人来夺取属于你的荣耀?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7102, 'zhCN', '你还没有攻陷任何一座哨塔，$N!你在等什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7121, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7122, 'zhCN', '你还没有占领一座矿坑，$N!$B$B只有控制了矿坑，我们才能方便地采集矿石和获取补给。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7123, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7124, 'zhCN', '你还没有占领一座矿坑，$N!找到一个还未被我们控制的矿坑，然后击败里面的首领!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7141, 'zhCN', '你拿到地图了吗，$N?那些地图上可能标明了更多神器的位置!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7142, 'zhCN', '你拿到计划了吗，$N?我们必须知道那些矮人为什么如此急于把他们的鲜血洒在战场上。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7161, 'zhCN', '醒醒！醒醒，$N！这不是做梦。你已经进入了战场中央！', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7162, 'zhCN', '小心那个地区的鹰身人，$C。它们会毫不留情地割破你的喉咙!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7163, 'zhCN', '许多人都没有活到摆脱平庸的那一天。你已经证明了自己是一个勇士的楷模，$C。现在是提升你的军阶的时候了。$B$B这是你的新徽记。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7164, 'zhCN', '很高兴再看到你，$C。我没有想到你能回来。啊，你还证明了自己是部落勇敢而光荣的勇士。$B$B现在又是嘉奖你的时候了，$N。你赢得了又一个新的军阶，因此你将获得一枚新的徽记。$B$B拿着吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7165, 'zhCN', '如此多的勇士献出了生命，鲜血染红了整个战场。然而，你活下来了……你还坚定地站在这里。你带领我们的部队取得了无数胜利，你做出了杰出的贡献，这为你赢得了更高的声望和军阶。$B$B这就是你的新徽记。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7166, 'zhCN', '在我的面前站着的是联盟所尊崇的英雄。$B$B<哈格丁中尉向你敬礼。>$B$B很少人能够在雷矛部族赢得这样的称号。我看到敌人向你蜂拥而来，旋即全部倒下。$B$B你踏入战场，就成了我们部队的明灯!$B$B这是你的新徽记。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7167, 'zhCN', '德雷克塔尔赞美着你的伟业，卡利姆多到处都在传颂你的事迹。大酋长很高兴―你为我们部落做出的贡献让他感到非常高兴。$B$B你将统御我们的部队。$B$B这就是你的新徽记，指挥官$N。$B$B<拉格隆德向你致敬。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7168, 'zhCN', '许多人都没有活到摆脱平庸的那一天。你已经证明了自己是一个勇士的楷模，$C。现在是提升你的军阶的时候了。$B$B这是你的新徽记。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7169, 'zhCN', '整个基地中都在传颂你在征战平原上英勇杀敌的事迹!你对我们的敌人造成了沉重的打击，把他们杀得四散逃窜!为此，你在雷矛部族中的军阶获得了提升。$B$B这就是你的新徽记。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7170, 'zhCN', '如此多的勇士献出了生命，鲜血染红了整个战场。然而，你活下来了……你还坚定地站在这里。你带领我们的部队取得了无数胜利，你做出了杰出的贡献，这为你赢得了更高的声望和军阶。$B$B这就是你的新徽记。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7171, 'zhCN', '在我的面前站着的是联盟所尊崇的英雄。$B$B<哈格丁中尉向你敬礼。>$B$B很少人能够在雷矛部族赢得这样的称号。我看到敌人向你蜂拥而来，旋即全部倒下。$B$B你踏入战场，就成了我们部队的明灯!$B$B这是你的新徽记。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7172, 'zhCN', '德雷克塔尔赞美着你的伟业，卡利姆多到处都在传颂你的事迹。大酋长很高兴―你为我们部落做出的贡献让他感到非常高兴。$B$B你将统御我们的部队。$B$B这就是你的新徽记，指挥官$N。$B$B<拉格隆德向你致敬。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7181, 'zhCN', '小心一点，士兵。雷矛部族也在猎捕科尔拉克。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7201, 'zhCN', '<搓揉着她的双手。>$B$B给我看看!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7202, 'zhCN', '小心一点，士兵。霜狼氏族也在猎捕科尔拉克。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7223, 'zhCN', '你带回护甲碎块了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7224, 'zhCN', '收集工作进行得怎么样，$N。你有没有可以给我的护甲碎块?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7241, 'zhCN', '嗯?又来一个新兵?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7261, 'zhCN', '<哈格丁中尉向你敬礼!>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7281, 'zhCN', '他一定非常痛苦，因为只有他独自活着，而他的兄弟已经升华到了一个完美的境界。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7282, 'zhCN', '我下达这个命令的时候心情非常沉重，士兵。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7301, 'zhCN', '有什么要让我知道的事情?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7302, 'zhCN', '任务进行得怎么样，士兵?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7321, 'zhCN', '寻找原料的运气好么？我告诉你，这个地区已经有太多联盟的杂碎...在我看来，他们已经走得太近了...', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7341, 'zhCN', '你好，$r。我是考拉克，来自雷霆崖的赏金猎人。我游历了整个世界，见识过很多奇异的事情。$B$B我穿越了整个南海，并偶然找到了这些奇妙的瑟银之箭。啊，我有无数的箭，但是我的子弹都用光了。呸，没有好的装备，一个牛头人怎么可能横行战场。这些箭现在毫无用处!$B$B我们来进行一个交易:你可以用瑟银弹来交换我的瑟银之箭。怎么样?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7342, 'zhCN', '你能相信吗?!刚从卡拉诺斯运来的这一批弹药都是劣等货!我们现在手里只有这些弓箭!我要用这500,000支瑟银箭来做什么?$B$B我告诉你，孩子。如果你可以给我弄些瑟银弹壳，我就给你瑟银箭作为交换―成交吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7361, 'zhCN', '他们想要灭绝我们?他们自己才应该被灭绝。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7362, 'zhCN', '我可不管头发是什么地方来的，$r。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7363, 'zhCN', '对，事实确实如此。我的兄弟是人类。这让我感到惭愧……这是我几乎完美的纪录上的一个污点。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7364, 'zhCN', '我倒要看看没有蹄子的牛头人还能耍什么花样。该死的东西……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7365, 'zhCN', '这是一个众所周知的事实:所有巨魔都带着魔精。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7366, 'zhCN', '本尼迪塔斯!我无法从他的光辉中找到一丝瑕疵。赞美他!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7367, 'zhCN', '你闻到了吗，$C?那是胜利的味道!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7368, 'zhCN', '你没看见我正在尽力避免把自己炸上天么?你有什么事?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7381, 'zhCN', '他回来了吗?那个嗜血的野兽又复活了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7382, 'zhCN', '他复活了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7383, 'zhCN', '神谕之树、大德鲁伊和德鲁伊们一起密切关注着泰达希尔的成长。但是虽然我们有了一个新家，但我们还没有重新得到不朽的生命。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7385, 'zhCN', '你可以为我提供更多敌人的鲜血。我很乐意接受更多，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7386, 'zhCN', '你可以避开硝烟弥漫的战场。在激烈的战斗之外，你可以帮我收集霜狼氏族的风暴水晶。$B$B议会正在收集这样的水晶，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7401, 'zhCN', '你是来观光的么，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7402, 'zhCN', '放松一点，士兵。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7421, 'zhCN', '继续保卫暗矛部族，$N。杀了那些夜精灵，把他们的头颅交给纳亚克。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7422, 'zhCN', '看来高等技工已经听说了我们参加奥特兰克山谷之战的事情。他已经下达了命令，要求其子民拿回牛头人的蹄子作为报复。这些地精比我想象得更难对付!我们必须进行更多的研究，我们需要更多地精的头发。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7423, 'zhCN', '你为那些人类做了一件好事，$N。继续拯救那些没有像我们这样幸运的种族吧。$B$B每块人类的骨头碎片都是一个可以被拯救的灵魂，我们可以让他们从冰冷无情、污秽而又压抑的躯体和生命中解脱出来。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7424, 'zhCN', '让他们个个都变成瘸子，$N。让他们知道地精可不是好欺负的!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7425, 'zhCN', '除非所有巨魔都永远沉默……否则鹿盔是不会满意的。继续从他们病态的躯体上收集魔精油吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7426, 'zhCN', '<指挥官菲力浦好像在哭泣。>$B$B这样的仁慈足以让任何人都潸然泪下。本尼迪塔斯是一个活生生的奇迹!$B$B继续传播他的仁慈吧，$N。还有许多被遗忘者的心脏等着你去焚烧净化!不要让他(或者我)失望。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7427, 'zhCN', '只要我有了足够的脊骨，就可以将它们都装载在我们的投石车上，然后射向丹巴达尔。$B$B雷矛部族将感受到分离和死亡的恐怖。我简直等不及了!$B$B我们还需要更多的脊骨。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7428, 'zhCN', '那些蛮子永远也不懂得吸取教训，$N。继续攻击那些兽人。霜狼氏族将会知道“痛苦”这两个字的含义，地精们的头发也得以保全。$B$B这就是我们所说的“双赢”战略。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7429, 'zhCN', '我说的是戈多克镣铐钥匙―好吧，那些胖家伙是这么叫它的。任何巨魔都有可能拿着这把钥匙，不过我不清楚到底谁拿着它。没准那些卫兵是负责看管钥匙的……$B$B啊呀，我到底在想什么?你已经拿到钥匙了，对吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7441, 'zhCN', '我无怨无悔，陌生的$r。女王很久之前就已经逝去，辛德拉的工作成果已经被那个托塞德林王子的疯狂毁灭了。我要的只是自由……我只有选择逃跑……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7461, 'zhCN', '你要小心，冒险者。王子非常善于……花言巧语。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7462, 'zhCN', '这是一只大箱子。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7463, 'zhCN', '什么?我认识你吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7481, 'zhCN', '我的同事，夜精灵的学者卢索恩.纹角正在找人帮助他来完成这项任务。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7482, 'zhCN', '我的同事，牛头人的先知科鲁拉克正在找人帮助他来完成这项任务。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7483, 'zhCN', '你有什么事?我还有很多事得做!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7484, 'zhCN', '你又来了?这一次你有什么事?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7485, 'zhCN', '我的耐心让你免受痛苦。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7486, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7487, 'zhCN', '现在跳入裂缝之中将会给你带来无穷的灾难……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7488, 'zhCN', '你拿到了网了没有，$N?我们必须驱散它所汇集的魔法能量!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7489, 'zhCN', '你拿到蕾瑟塔蒂丝的网了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7490, 'zhCN', '<萨尔看着奥妮克希亚那颗巨大的头颅，努力克制着自己的情绪，强大的末日之锤已经被他握在手中了。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7491, 'zhCN', '这是一种光荣，也是一种特权，英雄。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7492, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7493, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7494, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7495, 'zhCN', '以乌瑟尔的胡须的名义!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7496, 'zhCN', '你让暴风城重获了荣耀，年轻的勇者。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7497, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7498, 'zhCN', '你找到了一本书!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7499, 'zhCN', '你找到了一本书!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7500, 'zhCN', '你找到了一本书!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7501, 'zhCN', '你找到了一本书!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7502, 'zhCN', '你找到了一本书!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7503, 'zhCN', '你找到了一本书!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7504, 'zhCN', '你找到了一本书!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7505, 'zhCN', '你找到了一本书!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7506, 'zhCN', '你找到了一本书!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7507, 'zhCN', '啊，我们最伟大的书籍中的一卷。它已经遗失很久很久了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7508, 'zhCN', '太好了，让我们开始吧……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7509, 'zhCN', '也许有一天，会有一位勇士重新挥舞这把棱彩古剑。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7521, 'zhCN', '即使要毁灭整个世界都在所不惜!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7541, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7562, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7563, 'zhCN', '你找到那些疯狂的枭兽了吗，$N?那些生物拥有很强大的魔法能量，而它们那饱经折磨的躯体中流淌的血液就是这种能量的载体。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7564, 'zhCN', '你有东西要给我?难道是一只巨大而多汁的蜘蛛?或者是一只蟑螂?我希望它还是活的……我很喜欢嚼活蜘蛛或者蟑螂……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7581, 'zhCN', '只有荒野萨特之血才能满足要求。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7582, 'zhCN', '赫达琳恶魔绝对不会轻易放弃它们珍贵的宝石，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7583, 'zhCN', '嗯?干得怎么样了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7601, 'zhCN', '你的能力绝不比我的主人差。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7602, 'zhCN', '不要告诉别人，其实尼比是个傻瓜。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7603, 'zhCN', '你是\n不是迷路了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7604, 'zhCN', '我们之间有商业往来吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7621, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7622, 'zhCN', '完成你的使命，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7623, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7624, 'zhCN', '我仿佛已经可以尝到乌拉泰克心脏的味道了。叛徒的血肉真是美味……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7625, 'zhCN', '你去过加德纳尔了吗，$N?你有没有从贝恩霍勒领主那里弄到星尘?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7626, 'zhCN', '你拿到那些药剂了吗，$N?莫苏尔说你需要一只达斯莫拉之铃，我需要那些药剂才能制造充满暗影力量的东西!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7627, 'zhCN', '黑暗战车之轮并不容易制造，而且它也需要很多材料。你收集到了我所需的材料了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7628, 'zhCN', '你把龙鳞带来了吗，$N?在你带来鳞片之前我不会开始制作蜡烛……否则整个营地都会被炸上天的!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7629, 'zhCN', '你去过通灵学院了没有，$N?小鬼有没有用星尘制作羊皮纸?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7630, 'zhCN', '你拿到奥金了吗，$N?我需要这种材料来制作黑色磁石。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7631, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7632, 'zhCN', '<瓦特鲁斯呻吟着。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7633, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7634, 'zhCN', '欢迎回来，年轻的$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7635, 'zhCN', '我确信找一头黑龙然后干掉它并没有什么难度，$N。不要浪费我的时间。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7636, 'zhCN', '不要被它们的伪装所迷惑!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7637, 'zhCN', '你好，高贵的圣骑士!我已经准备好了我最珍贵的香炉，里面放入了最新鲜的熏香并施以祝福三次供你使用。你的捐献不但将帮助我们在铁炉堡制作一个新的香炉，还可以用于购买食物来帮助我们的人们。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7638, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7639, 'zhCN', '欢迎回来，$N。你得到驱魔香炉了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7640, 'zhCN', '很高兴再次见到你，$N。你在恐惧谷的工作完成了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7641, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7642, 'zhCN', '啊，$N。如果没有我说的那些东西，我是无法完成你的马饰的。你拿到所有东西了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7643, 'zhCN', '上古圣马之魂期盼地看着你，好像它希望从你这里得到什么。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7644, 'zhCN', '$N，你回来了!你有没有搞定梅丽德丝.卡尔森和特迪斯.扭木?老实说，我现在还不能确定哪一个威胁更大……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7645, 'zhCN', '怎么样，我证明你是错的了，是不是?我证明你们都错了!我一直以来都是正确的!!!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7646, 'zhCN', '你已经走到了这一步，$N。你为它所做的一切牺牲都将为你带来伟大的成就。我可以向你保证这一点。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7647, 'zhCN', '堕落的战骑之灵用悲哀的眼神凝视着你。它空洞的双眼中充满了绝望。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7648, 'zhCN', '你的任务进展得不错，$N。一旦你完成了马饰，就可以进入任务的下一步了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (7649, 'zhCN', '你找到了一本书!', 0);
