INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8238, 'zhCN', '我在寻找被称为部族硬币的权力珍宝。在过去，祖尔格拉布的居民们将它们作为流通货币使用，每一枚硬币都浸入了微量而强效的魔精。$B$B这些硬币分为9种。如果你有多馀的硬币，我可以按3枚硬币对应一枚荣誉勋章的价格跟你交换，然后你就可以用勋章去跟林沃斯换取某些特殊的物品了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8239, 'zhCN', '我在寻找被称为部族硬币的权力珍宝。在过去，祖尔格拉布的居民们将它们作为流通货币使用，每一枚硬币都浸入了微量而强效的魔精。$B$B这些硬币分为9种。如果你有多馀的硬币，我可以按3枚硬币对应一枚荣誉勋章的价格跟你交换，然后你就可以用勋章去跟林沃斯换取某些特殊的物品了。', 0);
