INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8269, 'zhCN', '我知道在战歌峡谷获得胜利并不是容易的事情。但是，你为我们所做出的贡献―即使你还没有获得胜利―对我们来说也很重要。$B$B如果你完成一次战歌峡谷中的试炼但是没有获得胜利的话，你还是可以获得一根奉献绶带。把它带给我，银翼哨兵会因你所做出的贡献而奖励你……即使你未能获得这次战役的胜利。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8275, 'zhCN', '我们正忙于准备进攻对付异种蝎，我希望你能前来协助我们。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8276, 'zhCN', '我们正忙于准备进攻对付异种蝎，我希望你能前来协助我们。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8277, 'zhCN', '你完成了样本收集工作吗?拯救诺格刻不容缓!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8278, 'zhCN', '这是我们最后一次救诺格的机会!快!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8279, 'zhCN', '是谁啊?喔，是你!你找到所有的词典章篇了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8280, 'zhCN', '掘泥打击者是一个很严重的威胁，必须马上解决他们!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8281, 'zhCN', '掘泥打击者是一个很严重的威胁，必须立即将他们解决!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8282, 'zhCN', '还没找到我的包包吗?不过我已经很幸运了，至少毒药已经被净化过。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8283, 'zhCN', '我需要那个生物的刺针做为证据。没有刺针，没有奖励。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8284, 'zhCN', '那个荒芜的营地就在这里的西北方。拿到所有的碎片后再回来。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8285, 'zhCN', '你!你怎么找到我的?你是谁?没人跟着你来吧，有吗?说话!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8286, 'zhCN', '安纳克罗斯回来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8287, 'zhCN', '你带给我的是什么, $N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8288, 'zhCN', '你必须赢得诺兹多姆的子嗣的好感。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8291, 'zhCN', '银翼要塞那边有什么消息吗?你有没有击败战歌氏族的部队，拿到你的功勋奖章?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8292, 'zhCN', '你已经证明了自己在战歌峡谷中的价值!继续帮助我们作战，并给我带来更多的功勋奖章。我们会因此而更加信任你。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8293, 'zhCN', '你已经证明了自己在战歌峡谷中的价值!继续攻打银翼要塞的哨兵部队，给我带来更多的功勋奖章!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8294, 'zhCN', '敌人的鲜血就是我们的荣耀，你干得很出色。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8297, 'zhCN', '你拿到资源木箱了吗，$N?如果没有足够的资源，阿拉索联军的士兵可没有办法好好干一仗……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8298, 'zhCN', '虽然在破坏阿拉索联军的补给线方面，污染者做得已经很不错了，但是联盟还在继续对阿拉希盆地进行攻击。我们必须向盆地增派部队，并夺回更多的资源。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8299, 'zhCN', '你赢得阿拉希盆地的战斗了吗，$N?你拿到资源木箱了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8300, 'zhCN', '虽然在破坏阿拉索联军的补给线方面，污染者做得已经很不错了，但是联盟还在继续对阿拉希盆地进行攻击。我们必须向盆地增派部队，并夺回更多的资源。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8301, 'zhCN', '它们之所以不信任人类种族是有深厚的历史因素的;不过，唉，这种故事还是留给他的子嗣在对的时机来讲吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8302, 'zhCN', '说不定有一天整个塞纳里奥要塞都会在你的控制之下。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8303, 'zhCN', '<安纳克罗斯点点头。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8304, 'zhCN', '别浪费时间了，$N!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8305, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8306, 'zhCN', '娜塔莉雅?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8307, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8308, 'zhCN', '喔，亲爱的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8309, 'zhCN', '如果你在其中一个虫巢里发现一些疯狂的夜精灵小姑娘，别忘了替我们给她点颜色瞧瞧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8310, 'zhCN', '去和猴子玩吧，小鬼。我现在没空。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8311, 'zhCN', '你完成“不给糖就捣蛋”了吗?我很遗憾我生病了不能去，不过你的帮忙让我感觉好多了…', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8312, 'zhCN', '你完成“不给糖就捣蛋”了吗?我很遗憾我生病了不能去，不过你的帮忙让我感觉好多了…', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8313, 'zhCN', '你学得食谱了吗?你不会想占为己有吧，我希望!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8314, 'zhCN', '天啊!你手上的是什么东西，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8315, 'zhCN', '你一定要在机会出现时奋力一击!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8317, 'zhCN', '丸子做的怎么样了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8318, 'zhCN', '你找到那些纸片了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8319, 'zhCN', '继续带文件给我。我们很快就能逼暮光之锤回到他们爬出来的巢穴了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8320, 'zhCN', '进入沙漠寻找暮光地领主，$N。杀了他们之后回来找我。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8321, 'zhCN', '$N,你打败卑鄙的维拉尔了吗?有没有拿到他的玺戒呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8322, 'zhCN', '桶子里装满了麦芽酒。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8323, 'zhCN', '你有找到任何加密文件吗?我对阅读这个月的真实信仰者感到非常好奇!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8324, 'zhCN', '有找到更多的加密文件吗? 带给我一大批这些文件，解码的工作会花上一些时间。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8325, 'zhCN', '我们重整社会的工程就在这里展开，$N。当我们救回我们的家园，就能扬眉吐气了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8326, 'zhCN', '要你杀死这些野兽对我来说也不是件快乐的事。过去我们也与这些生物和平共处，现在情况已大不相同了。对所有的辛多雷而言，最重要的一个任务就是生存;记住这一点。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8327, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8328, 'zhCN', '啊， $N - 我想你是这个小岛上的一个新法师，对吗？ 嗯，你来对地方了。 如果你寻求的是掌握奥术，那我可以给你提供这些知识 - 只要你有足够的资金来处理培训费用和专注我所教课程的能力。 我们开始吧！', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8330, 'zhCN', '你找到了我的东西吗?只要我们重拾逐日岛的统治权，我就会需要这些东西。至于现在，我必须继续看守着太阳之井……或是它剩下的土地。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8331, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8332, 'zhCN', '你拿到裂片和羽饰了吗，$N?我需要它们才能制作暮光信徒身份勋章。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8333, 'zhCN', '如果你还需要另一个暮光信徒身份勋章，我很乐意效劳。不过，我还需要另一块艳丽的裂片和一些深渊纹章。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8334, 'zhCN', '你最好多了解一点生存的意义 - 还有生存的代价 - 早点知道比较好。身为一个血精灵，作困难的决定是你即将习惯的事。$B$B当天灾军团将我们的家园一分为二的时候没有人伸出援手。是我们，我们自己重新振作，东山再起。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8335, 'zhCN', '记住这个警告 -', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8336, 'zhCN', '岛上许多生物都因为我们的魔法忽然变的无侵略性又温驯。在天灾军团摧毁太阳之井后，我们对这些生物的控制就被粉碎了。秘法棱片是我们曾掌握控制的一点馀烬，或许能从中想出一个新对策，让我们重拾对这些生物的控制权。$B$B更顺利的话，或许棱片还能让我们知道这座岛到底是为何而抑郁。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8338, 'zhCN', '从你的举止看来，好像有什么急事。有什么特别的事我可以帮你吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8341, 'zhCN', '你拿到裂片和羽饰了吗，$N?我需要它们才能制作一个贵族之戒。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8342, 'zhCN', '$N，如果你打算再次联系深渊高等议会，那么你需要另一枚暮光徽记之戒。带更多的裂片和羽饰给我，我就能为你制作一枚戒指。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8343, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8344, 'zhCN', '怨灵精华会是很好的来源实验品。身为一个术士，在新事物扭曲我们的意志前从中学习，对你们而言是很重要的。恶魔是最重要的能量泉源，如果这岛上真有这样的污染会是很好的利益。$B$B嗯…是我们术士的利益。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8345, 'zhCN', '你已经读过神殿中的铭文了吗，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8346, 'zhCN', '这不是一个选择题…你一定要抑制对魔法的渴望。如果失败了就会像血精灵一样。他们以为自己可以不受折磨地控制自己的需求，现在也只是跟那些失落者一样罢了。$B$B你不会想象一个发疯的上瘾者一样结束生命的。相信我。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8347, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8348, 'zhCN', '你跟深渊公爵进行得如何?它打人很痛吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8349, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8350, 'zhCN', '欢迎来到我的旅店，$C。你有什么东西要给我吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8351, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8352, 'zhCN', '嗯，事情进行得如何?我会不计代价让自己离开这个地点并与它们搏斗的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8355, 'zhCN', '嗯，开始吧…', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8356, 'zhCN', '你不展示肌肉是不会有糖果的，$N……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8357, 'zhCN', '你会跳舞吧?先伸你的右脚…再缩你的右脚…伸你的右脚…扭扭身子…', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8358, 'zhCN', '嗯，开始吧……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8359, 'zhCN', '你不展示肌肉是不会有糖果的，$N……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8360, 'zhCN', '你会跳舞吧?先伸你的右脚…再缩你的右脚…伸你的右脚…扭扭身子…', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8361, 'zhCN', '你完成工作了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8362, 'zhCN', '你已经证明了自己，$N。让我们继续给暮光之锤施压吧。我需要你去击杀更多的深渊圣殿骑士!我会奖励你一些我和胡姆在战斗中找到的东西。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8363, 'zhCN', '你证明自己可以应付深渊公爵，$N。我总是鼓励好的习惯。因此记得给我带来更多的徽记，我会给你相应的奖励。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8364, 'zhCN', '你已经完全超越了我的期望，$N。你干掉了我和霍姆一直以来都不敢面对的敌人。$B$B现在我能做的就是继续奖励你对抗暮光之锤的行为。$B$B带给我更多的深渊权杖，然后我将尽力给你最好的奖励。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8365, 'zhCN', '你收集到20顶帽子了吗?我不相信你，让我看到那些帽子。它们最好保存完好!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8366, 'zhCN', '那么快就回来了?我希望你好好教训了那帮无赖，让他们一辈子都无法忘记。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8367, 'zhCN', '阿拉希到处流淌着战士的鲜血，奥特兰克的金属碰撞声不绝于耳。除非你从前线带回了消息，否则不要浪费我的时间!', 0);
