INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8385, 'zhCN', '啊，$N。我希望你在各个战场的前线传授你的经验。许多刚刚来到战场上的小伙子和小姑娘都非常尊敬你这样的老兵。', 0);
