INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8556, 'zhCN', '你帮我把戒指和材料带来了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8557, 'zhCN', '你帮我带来了披风的材料吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8558, 'zhCN', '你把我要的材料带来了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8559, 'zhCN', '你把我要的东西都带来了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8560, 'zhCN', '你把我要的材料带来了吗，$N?', 0);
