INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8562, 'zhCN', '在安其拉的住所里，可怕的克苏恩在睡眠中等待着。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8563, 'zhCN', 'Yes?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8564, 'zhCN', 'Yes?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8565, 'zhCN', '啊，对!我们当然不会忘记你过去在阿拉希盆地的成就，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8566, 'zhCN', '啊，对!我们当然不会忘记你过去在阿拉希盆地的成就，$N。', 0);
