INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (8747, 'zhCN', '相当出色，小家伙。你吃了不少苦才获得青铜龙军团的青睐。你的奉献已被记录下来。$B$B防卫者的徽记之戒将会在你对抗邪恶力量时提供更多的保护。$B$B提高警觉，一旦你选择了你的道路，就无法回头了。', 0);
