INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9385, 'zhCN', '你在处理劫毁者威胁上有所斩获了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9386, 'zhCN', '你找到你在黑暗中的路了吗?', 0);
