INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9396, 'zhCN', '你取得了卷轴吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9397, 'zhCN', '你回来了。你带回了母卡里瑞鸟吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9398, 'zhCN', '欢迎回来，$N。你完成了我要你做的任务吗?', 0);
