INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9401, 'zhCN', '告诉我，刺客死了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9402, 'zhCN', '叫你潜水的事情进行的如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9403, 'zhCN', '只有从艾兰达瀑布取得之最纯净的水才能被跟法术一起使用。你拿到它了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9404, 'zhCN', '你拿到了吗?快把它交给我!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9405, 'zhCN', '告诉我你在找了什么，我可以帮助你找到它。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9406, 'zhCN', '你带了什么来给我们，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9409, 'zhCN', '嗨，$C。你拿着什么?是给我的吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9410, 'zhCN', '你还活着真的是很幸运。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9415, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9416, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9417, 'zhCN', '你回来了!如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9418, 'zhCN', '我一直盼望的自由终于是我的了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9419, 'zhCN', '在那里动作要小心，但也要快速。我们没有时间可以浪费。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9420, 'zhCN', '你把卡里瑞羽毛带回来给我了吗，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9422, 'zhCN', '小心沙漠里的危险，$N。有联盟在沙丘里埋伏，你的任何一步都可能是最后一步。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9424, 'zhCN', '你代替玛酷鲁替希戴报仇了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9426, 'zhCN', '你完成你的任务了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9427, 'zhCN', '你回来了，$N。这些水已经净化了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9429, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9430, 'zhCN', '你拿到夏纳圣物了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9431, 'zhCN', '博学者伊罗赛的笔记有点隐秘，但我想我了解她想告诉我的话。如果你有她说的泥土，我就能将它放进你需要用来取月井之水的容器中。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9432, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9433, 'zhCN', '它照做了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9434, 'zhCN', '你和维兹罗收集好试剂了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9435, 'zhCN', '你从被遗忘者那里夺回了水晶吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9436, 'zhCN', '别让我失望，$N。如果我们可以让这个血顶魔法屈服在我们的意志之下，我的人民就能消灭阿曼尼威胁了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9437, 'zhCN', '你从那些光鲜亮丽的私掠船员手中救出了伊萨妮亚并取回我的货物吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9438, 'zhCN', '你从外域带来消息，$C?快说!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9439, 'zhCN', '看在逐日者的份上，别盯着我看!我被迫穿着这件肮脏的衣服就已经够糟的了!在我经历过这么多事后，我最不希望的一件事就是被人盯着看。你从那些黑铁矮人称为堡垒的狭小地窖收集到我的财产了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9440, 'zhCN', '你把所有的食物都喂了他们被捕捉的动物吗?慢着!你没有给关在笼子中那些愚蠢的人类吃吧?，你有吗?$B$B喔，很好!不然真是浪费食物!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9442, 'zhCN', '你取得了我需要的蘑菇吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9443, 'zhCN', '等你取得了他的\'记号\'，我们就要把它做些改变;让它变的更好。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9444, 'zhCN', '那么，完成了?乌瑟之墓被亵渎了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9446, 'zhCN', '你不是该和隐士.楚恩一起走到乌瑟之墓吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9447, 'zhCN', '你回来了，$N。你的任务完成了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9448, 'zhCN', '你做了正确的事，$C。失落者已经受了这么多苦难。他们不该再忍受这样的屈辱。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9449, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9450, 'zhCN', '在我看来元素还是处在失衡状态，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9451, 'zhCN', '破碎者和德莱尼会一起重获对元素秘密更深的了解。$B$B你带回制造大地图腾所需的物品了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9452, 'zhCN', '小心鱼人:奇怪又特别讨人厌的小生物。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9453, 'zhCN', '你觉得我的护甲怎么样?我自己制作的!这和我们习惯上穿的不太一样，但却极为舒服。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9454, 'zhCN', '成鹿皮革容易损坏也因此形成柔软多汁的鹿肉。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9455, 'zhCN', '你找到了什么?在哪里?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9456, 'zhCN', '我们不能冒险让感染继续扩散。我从血色守望收到的报告已经够糟了。这一定要现在就被阻止!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9457, 'zhCN', '有奈亚斯的消息吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9460, 'zhCN', '你弄到了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9461, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9462, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9463, 'zhCN', '我担心她的病况正持续恶化中。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9464, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9465, 'zhCN', '你拿到火炬了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9466, 'zhCN', '你有黑爪的消息?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9467, 'zhCN', '没拿到郝特的骨骸就不要来跟我说话。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9468, 'zhCN', '你的衣服边缘看起来有些烧焦。你和火焰的交流进展的如何，萨满?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9469, 'zhCN', '羽须看来不在家，但或许他的日记会透露找到他的线索。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9470, 'zhCN', '你带着巫医梅金的死讯回来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9471, 'zhCN', '你削减了银鬃的数量吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9472, 'zhCN', '嗯，$N。完成了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9473, 'zhCN', '别浪费时间，$g男孩:女孩;。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9474, 'zhCN', '光明使者的印记是乌瑟对圣光信念的一个强大象征。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9475, 'zhCN', '你脸上的表情说明你带来了坏消息。你发现了什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9476, 'zhCN', '这具骷髅是枯木献祭受害者残存的遗体。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9483, 'zhCN', '就因为我们参与这次的朝圣并不代表我们要放弃所有生命的乐趣。距离我上次喝杯好酒已经是好几世代前的事了。$B$B为了一瓶塞纳里奥烈酒要我杀人也无所谓!$B$B我知道，我知道，我怎么会这么落伍喜欢喝夜精灵酿的酒。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9484, 'zhCN', '你制服龙鹰了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9485, 'zhCN', '你驯服迷雾蝙蝠了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9486, 'zhCN', '你和泉爪山猫一起打猎了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9487, 'zhCN', '你没靠那些畜生太近吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9488, 'zhCN', '没有必须的材料就无法完成长袍。但你知道这点，对吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9489, 'zhCN', '你给予他们希望了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9490, 'zhCN', '你带回黑爪死亡的证据了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9491, 'zhCN', '请告诉我你找到戒指了。那个商人不久后就会找上他自己的盗贼了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9492, 'zhCN', '你带回“大酋长”卡加斯.刃拳死亡的证据了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9493, 'zhCN', '我不会天真到认为击败了破碎之手军团士兵就预告了我们对魔狱部落的胜利，但这的确是个很好的开始。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9494, 'zhCN', '<魔导师萨柏瑞希斯扫视了一下艾咪希.迷雾长者并压低她的声音。>$B$B你把，呃，馀烬带来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9495, 'zhCN', '你有卡加斯.刃拳战败的证据吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9496, 'zhCN', '一旦破碎之手军团士兵被击破，我们就能开始进展对付魔狱部落的战役。你完成你的任务了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9498, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9499, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9500, 'zhCN', 'Yes?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9501, 'zhCN', 'Yes?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9502, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9503, 'zhCN', '你这么快就从我不幸的姐妹身上收集好腐败精华了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9504, 'zhCN', '你从废墟弄来纯净的水了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9505, 'zhCN', '你是哪种野兽?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9506, 'zhCN', '运气如何，陌生人?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9508, 'zhCN', '你成功了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9509, 'zhCN', '元素对我说了你完成的伟大事绩。来，让我们谈谈。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9512, 'zhCN', '啊……呃……喔天啊……嗯，抱歉$g小伙子:小姑娘;，我要练习一下海贼的事免得我忘记……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9513, 'zhCN', '你为我的人民做了一件好事，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9514, 'zhCN', '你找到什么了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9515, 'zhCN', '保持警惕!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9516, 'zhCN', '那些恶魔还存在吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9517, 'zhCN', '<长者一边转向你一边发出咯吱声。>$B$B建筑师尼莫斯说你会从伐木者营地和污染的萨提纳尔带来木材。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9518, 'zhCN', '只要监督者高萨克还活着，我就会对抗他。伐木场有什么消息?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9519, 'zhCN', '你感觉到了吗，$N，腐败的气息弥漫在风中?如果我们什么都不做，它就会持续进展。这就是为什么我要寻找艾露恩酒杯的原因。你可以找到它吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9520, 'zhCN', '你手里拿着什么，$g先生:小;?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9521, 'zhCN', '你手里拿着什么，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9522, 'zhCN', '我们的夜精灵斥候报告攻击的准备似乎仍在进行。那些恐惧领主一定要立刻死掉，$N!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9523, 'zhCN', '没拿到古代圣物就不要回来。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9525, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9526, 'zhCN', '你还没把我全部的种子都种下去吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9527, 'zhCN', '我的妻子叫萨尔瑞莎，我的女儿是麦格文。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9528, 'zhCN', '嗨，$N。', 0);
