INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9560, 'zhCN', '他们是丑陋的小生物。就连盲目者麦格特都能看见他们……他是盲人。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9561, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9562, 'zhCN', '一只鱼人从我后面踢我一脚还对我吐口水……就在光天化日之下!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9563, 'zhCN', '苦啤酒很难找吗?守望堡的老波尼应该有储存几桶。$B$B除非他的都喝光了……那就太悲惨了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9564, 'zhCN', '这是我想的那个东西吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9565, 'zhCN', '就你所看见的，这些水晶被放置在这里 - 或许是枭兽放的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9566, 'zhCN', '水晶?它们是红的吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9567, 'zhCN', '你成功的取得对纳兹维斯萨特的了解吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9569, 'zhCN', '<复仇者艾乐希雅像是看着你的后方而不是看着你，她的心思系着那些萨特。>$B$B你有照着我的话把萨特领导者给铲除吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9570, 'zhCN', '<克兹咕哝着对你说。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9571, 'zhCN', '哈罗，英雄!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9572, 'zhCN', '是$N，对吗?史托克顿告诉我要等你的报告。$B$B我相信你带来了好消息?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9573, 'zhCN', '任务完成了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9574, 'zhCN', '你拿到我要的样本了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9575, 'zhCN', '是$N，对吗?查德威克告诉我要等你的报告。$B$B你来找我有什么事吗?!你的任务完成了吗?你是个彻底的白痴吗?你让我很想吐!$B$B现在把你无用的身躯拿来对付防御，做你该做的事!听见了没?!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9576, 'zhCN', '你手里拿着什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9578, 'zhCN', 'Yes?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9579, 'zhCN', '<莫瑞伊怀抱希望的看着你。>$B$B你的搜索有找到高拉恩的任何蛛丝马迹吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9580, 'zhCN', '打猎进行的怎么样，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9581, 'zhCN', '你收集到第一个水晶样本了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9582, 'zhCN', '你解决掉那个野兽了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9584, 'zhCN', '你弄到第二个水晶样本了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9585, 'zhCN', '你带着我要的最后一个样本回来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9586, 'zhCN', '你找到塔瓦拉了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9587, 'zhCN', '你拿着什么东西吗，$g年轻人:女士;?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9588, 'zhCN', '你手里拿着什么信，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9589, 'zhCN', '你拿到那些血了，亲爱的?如果我们要你去弄到手那就一定是很重要的，你知道吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9590, 'zhCN', '你没这么快就收集好血液了吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9591, 'zhCN', '你能驾驭图腾的魔法和尖刺的爬行者做朋友吗?他们是烦躁不安的小生物!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9592, 'zhCN', '林地陆行鸟是一种强大的鸟类，掠夺者常判断错误而导致悲惨的结局。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9593, 'zhCN', '夜间潜猎者基本上只是有剃刀般的锐爪和爱吃肉的大猫咪而已……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9594, 'zhCN', '什么事，$C?你拿着什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9595, 'zhCN', '你挑战了你的梦魇吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9598, 'zhCN', '欢迎回来，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9600, 'zhCN', '所有的生命都值得被拯救，只要不与我们作对就好。只有我们的敌人才要永远安息。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9601, 'zhCN', '你想要什么，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9602, 'zhCN', '你回来了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9603, 'zhCN', '你好，$N。你有兴趣租一只角鹰兽吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9604, 'zhCN', '你好，朋友。看来你长途跋涉了许久。我能帮你什么忙吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9605, 'zhCN', '你要去哪，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9606, 'zhCN', '欢迎回来，$N。鲁古尼把单子上的所有东西都收集好了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9607, 'zhCN', '$T平民:平民;，报告!你调查了整个鲜血熔炉了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9608, 'zhCN', '你调查完整个鲜血熔炉了吗?你有什么消息要告诉我?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9609, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9610, 'zhCN', '我期待着你收集好足够的阿塔莱神器，这样我就能开始研究它们了。我迫切的想了解他们曾经伟大的文明究竟发生什么事了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9612, 'zhCN', '我听说了你的一些好话，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9616, 'zhCN', '什么事?我很忙，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9617, 'zhCN', '又派了一个愿意学习的人来找我吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9618, 'zhCN', '你找到卡特斯了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9619, 'zhCN', '你沿着虚无石显现出来的路走了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9620, 'zhCN', 'Yes?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9621, 'zhCN', '我听说奎尔萨拉斯有个访客要来。我以为是个血精灵，老实说。$B$B你带来了什么消息?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9622, 'zhCN', '你回来了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9623, 'zhCN', '<托拉利斯调整他的眼罩注视着你。>$B$B这是我们能提供阿古斯之手最好的人吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9624, 'zhCN', '沙梨越来越难找，当然，这也表示伊莱克的派和飨宴也越来越少。你可以收集到所有我需要的水果吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9625, 'zhCN', '<渥卡恩调整他的眼罩。>$B$B眼罩?几年来的管理伊莱克会对你做这样的事……伊莱克管理是一件严肃的事!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9626, 'zhCN', '你是来见我的吗，血精灵?有话快说。我没有时间来你们种族那套繁文缛节。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9627, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9628, 'zhCN', '你知道是什么让调查小组无法回来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9629, 'zhCN', '仪器有用吗?你为我的研究标记好鱼人了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9631, 'zhCN', '艾特罗斯派你来的。星星已预测你的到来。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9632, 'zhCN', '你好，$C。你带了什么来?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9633, 'zhCN', '欢迎来到奥伯丁，朋友。你怎么会来黑海岸呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9634, 'zhCN', '我们一定要在劫毁者开始取代秘血岛当地的捕食者前采取行动。你开始削减劫毁者的数量了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9635, 'zhCN', '你弄到那些零件了吗?我要先忙着潜水。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9636, 'zhCN', '你弄到那些零件了吗?在隐士阿浩恩把我脖子扭下来之前我有一个压缩线圈要修理!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9637, 'zhCN', '你弄到我要的秘典了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9638, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9639, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9640, 'zhCN', '你不是在找一本日记还是什么的吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9641, 'zhCN', '你从秘血岛的生物身上收集到水晶裂片了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9642, 'zhCN', '如果你有更多受到辐射的水晶裂片，我很乐意和你交换。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9643, 'zhCN', '你弄到蟒藤了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9644, 'zhCN', '你带来了什么消息呢，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9645, 'zhCN', '你回来了!你带来了什么消息?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9646, 'zhCN', '有什么我能帮忙的吗，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9647, 'zhCN', '你剔除掉那些蛀虫了没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9648, 'zhCN', '记住，我所要的蘑菇都是秘血岛上独特的品种。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9649, 'zhCN', '这么快就回来了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9663, 'zhCN', '没有时间松懈了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9664, 'zhCN', '等哨塔在我们的控制之下时，我们很快就能包围被遗忘者。在那之后，粉碎他们就是迟早的事了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9665, 'zhCN', '他们一定是白痴才认为我们会让他们在离我们主城那么近的地方创建基地。$B$B我们会赶尽杀绝直到他们夹着尾巴逃回南方去。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9666, 'zhCN', '完成了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9667, 'zhCN', '我时间不多了，$N。他们很快就会杀了我……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9668, 'zhCN', '啊，$N，我听过你的很多事情!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9669, 'zhCN', '那是场可怕的灾难……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9670, 'zhCN', '运气如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9671, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9672, 'zhCN', '且慢，你是谁?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9673, 'zhCN', '啊，这是什么?一个中尉的学生?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9674, 'zhCN', '他们是恶心的生物……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9675, 'zhCN', '你看起来很想学习，年轻的$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9676, 'zhCN', '我看见你胸膛里与日俱增的使命感。你已想象自己是个血骑士。$B$B随着时间过去，你会在我们之中赢得你的地位。但首先你必须学会驾驭和控制圣光的力量，磨练你的武器技能，证明你的价值。$B$B你一定要准备好包容那些你发誓要保卫的人们对你的嘲笑，恐惧，和嫌恶。因为有些人并不明白一直以来守护着大众的我们背后所付出的代价。抬起你的头来，随时准备好你的剑刃，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9677, 'zhCN', 'yes?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9678, 'zhCN', '愿圣光的力量在你每次的奋战中都为你所用。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9680, 'zhCN', '你取回我要的骨头碎片了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9681, 'zhCN', 'yes?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9682, 'zhCN', '不必为他们的死亡哀悼，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9683, 'zhCN', '一定要把阿托夫引诱出来!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9684, 'zhCN', '你和博学者阿斯塔乐.血誓谈过了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9685, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9686, 'zhCN', '血骑士宁死也不会向敌人屈服。记住这点，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9687, 'zhCN', '龙的战争已过去好久了。所剩仅有骨头和被激怒的伊瑟拉后裔残存者。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9688, 'zhCN', '看到这种情景让我感到很痛苦。拜托，我们不要再谈下去了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9689, 'zhCN', '我建议你多带几名盟友一同转至，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9690, 'zhCN', '欢迎来到这里，新人。我期待等你完成试炼之后，我们就会给予你一个新的称谓了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9691, 'zhCN', '你的归来代表你成功的完成了试炼。可喜可贺，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9692, 'zhCN', '你取得所有要你拿到的东西了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9693, 'zhCN', '嗨，高尚的$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9694, 'zhCN', '他们数量太多以致于我认为他们好像是以倍数成长。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9696, 'zhCN', '我现在有点忙，密探$N。你要说的事最好很重要。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9697, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9698, 'zhCN', '<费伦微笑。>$B$B你好，$N。我一直在等你。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9699, 'zhCN', '怎么样呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9700, 'zhCN', '我听过传闻说那区域有奇怪的生物出现。不管他们是当地的突变动物或是真正的虚无反常者都是你要去调查的事情。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9701, 'zhCN', '这么快就回来了?你对孢子村的孢子人观察对我的研究是非常重要的。我希望你不是敷衍了事的那种人。展现一点耐心确定你探勘了整个重生峡谷再给我你最后的报告。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9702, 'zhCN', '你一定要小心地收集所有的证据，$C!我们正在拼凑一个很大的谜题。你犯一个错就会让我们导出错误的结论。$B$B那么我们对赞加沼泽的生态学能有多少了解呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9703, 'zhCN', '消息正确吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9704, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9705, 'zhCN', '你发现了什么，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9706, 'zhCN', '你手上拿的是什么东西，$N?那是一本日志吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9707, 'zhCN', '恭喜，$N。达到高手的阶级不是件简单的事，相信我。我见过许多进入那栋建筑物的人，却只有很少人撑的够久可以带着这些东西来见我。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9708, 'zhCN', '你弄到那些样本了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9709, 'zhCN', '你带回了所有我们帮泥沼领主规划的新菜园所需要的蘑菇吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9710, 'zhCN', '你准备好收下你的武器了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9711, 'zhCN', '马帝斯的士兵不会独自旅行。在外面要小心。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9712, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9714, 'zhCN', '我们仍然需要从盘牙蓄湖的幽暗沼泽跛行者，泥沼巨人和幽暗沼泽蝙蝠那里弄来更多的血红木槿。帮我以一束五朵的数量带来我会很感激你。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9715, 'zhCN', '你取得所有我要你收集的木槿花了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9716, 'zhCN', '你发现了什么吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9717, 'zhCN', '幽暗沼泽是个非常危险的地方。如果你不敢去那里我也不会说你是懦夫的。但如果你没带着我要的东西回来，我也不会感谢你的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9718, 'zhCN', '嗨，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9719, 'zhCN', '你回来了。这表示黑色阔步者已不复存在了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9720, 'zhCN', '完成了吗，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9721, 'zhCN', '欢迎，$N。你知道为什么我要和你谈谈吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9722, 'zhCN', '你说什么，$N?你想争取成为一位大师?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9723, 'zhCN', '你带着博学者阿斯塔乐.血誓要的材料回来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9724, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9725, 'zhCN', '欢迎回来，$N。你击溃了天灾军团和他们的血尸战车吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9726, 'zhCN', '你去拜访过那些纳迦了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9727, 'zhCN', '你去拜访过那些纳迦了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9728, 'zhCN', '再见到你真好，$N。你猎捕纳迦的事进行的如何了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9729, 'zhCN', '费斯伍尔还好吗?你取回方舟了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9730, 'zhCN', '你想谈谈关于洛迦海卡斯特的赏金?嗯，他死了吗?没什么好说的了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9731, 'zhCN', '你带来了什么消息，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9732, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9735, 'zhCN', '你弄到水了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9736, 'zhCN', '你收集到我要的材料了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9737, 'zhCN', '你有照我的话做并传达了我的讯息吗?世界上的假圣骑士都一定要知道他们的时代已经结束了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9738, 'zhCN', '你知道我那四个同伴的下落了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9739, 'zhCN', '孢子囊!你拿到它们了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9740, 'zhCN', '你一定要先毁掉他们的传送门控制器才能摧毁传送门!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9741, 'zhCN', '虚无异常者变成虚无反常者。说实话，我们真的不知道这些生物是什么……名字只是一个分类的方法。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9742, 'zhCN', '你救了更多的孢子囊吗，$r?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9743, 'zhCN', '泥沼领主杀的怎么样了?教那些畜生不要再进入孢子人的领土!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9744, 'zhCN', '你杀了许多巨人，但还有更多随侍在后。他们仍在攻击我们的孢子囊!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9746, 'zhCN', '他们的领导者不可能永远躲着我们。我们会有系统地消灭所有可能的藏身地点，直到找到赛罗娜斯为止。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9747, 'zhCN', '你回来了;卡塔鲁和昂布拉凡奴隶死了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9748, 'zhCN', '不要喝那个水，$N!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9749, 'zhCN', '运气如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9751, 'zhCN', '且慢，你是谁?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9752, 'zhCN', '嗯，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9753, 'zhCN', '我一直在等你。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9756, 'zhCN', '没有成功就不要回来!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9757, 'zhCN', '哈罗，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9758, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9759, 'zhCN', '完成了吗?我们胜利了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9760, 'zhCN', '你是主教阿德密特斯本来要派来的密探吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9761, 'zhCN', '你有在这附近看到勇敢又强大的不寻常的德莱尼吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9762, 'zhCN', 'Yes?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9763, 'zhCN', '卡利斯瑞怎么样了?他死了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9764, 'zhCN', '你手里拿着什么，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9765, 'zhCN', '你弄到我要的装备了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9766, 'zhCN', '我们会将纳迦自己的钢铁拿来铸造成武器对付他们。帮我把你在盘牙蓄湖找到的装备带来给我。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9769, 'zhCN', '那是什么?你被萤光蛾咬啦?$B$B<玛卡莎对她自己的笑话咯咯笑个不停。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9770, 'zhCN', '我还是看的见沼牙生物，$N。你当然没那么快就完成。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9771, 'zhCN', '这具憔悴的尸体和祖瑞伊描述的斥候杰巴一样。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9772, 'zhCN', '你回来了，但杰巴没跟你一起回来。他怎么了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9773, 'zhCN', '告诉我你没有空手回来，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9774, 'zhCN', '我看到你在奸笑了。是的，我的护甲在所有的旧金属都替换完成前都会看起来很奇怪，但这样总比毫无防护来的好。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9775, 'zhCN', '你看起来不像是我的人。说吧，$r。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9776, 'zhCN', '只有隐士阿浩恩知道我们避难所的位置。你是他的盟友之一吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9777, 'zhCN', '照情况来看，我们需要很快的补充蘑菇。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9778, 'zhCN', '你想和我说话吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9779, 'zhCN', '这么快就回来了?你找到书信了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9780, 'zhCN', '你有任何鱼肉可贡献给我们储藏吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9781, 'zhCN', '打猎进行的怎么样，朋友?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9782, 'zhCN', '你拿到死亡污泥的泥土样本了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9783, 'zhCN', '毁灭死亡污泥的生物让我很痛苦，但我们也救不了他们。我们能做的就是努力让该区域复原，养育一个新世代。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9784, 'zhCN', '我还需要更多的植物组织。随着湖泊的水分流失，我有预感入侵当地生态的品种数量正不断的攀升。不过，我需要更多的数据来支持我的理论。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9785, 'zhCN', '你和古树谈过了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9786, 'zhCN', '你探勘过波哈姆废墟了吗?你能想象他们全盛辉煌的时期是什么样子吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9787, 'zhCN', '你拿到塑像了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9788, 'zhCN', '你找到我的东西了吗，$N?那洞穴离昂布拉凡不远，就在纳格兰的边界附近。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9789, 'zhCN', '纳格兰的裂蹄是个很好的挑战。我已经猎捕到好一些了。$B$B你进行的怎么样?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9790, 'zhCN', '你收集到那些萤光蛾翅膀了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9791, 'zhCN', '你的沼牙打猎进行的顺利吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9792, 'zhCN', '<纳霍德谨慎地打量你。>$B$B你怎么会来泰拉，陌生人?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9793, 'zhCN', '我能帮你什么忙吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9794, 'zhCN', '<齐亚隆怀疑地看着你。>$B$B我不认识你，$r。你有事找我吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9795, 'zhCN', '我不认识你，$r。你怎么会来雷神要塞?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9796, 'zhCN', '你从祖瑞伊那带来什么消息?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9797, 'zhCN', '你是从萨布拉金来的吗?那么卡兹瑞克的工作已经完成了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9798, 'zhCN', '你拿着什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9799, 'zhCN', '哈罗，$C。你弄到那些花了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9800, 'zhCN', '<元素师罗艾柏做出两个拳头对立的样子并不同意的摇摇头。>$B$B巨魔般尺寸的拳头……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9801, 'zhCN', '你收集到材料了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9802, 'zhCN', '你有找到任何植物组织吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9803, 'zhCN', '你和菲拉芬的长者谈过了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9804, 'zhCN', '在你还没潜入太深的湖底时使用纳格兰樱桃。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9805, 'zhCN', '我非常惭愧的告诉你，是我的族人想在纳格兰的水源建造沼泽地。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9806, 'zhCN', '你回来了，$N。你取得了丰饶孢子吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9807, 'zhCN', '我们永远都需要更多的丰饶孢子，$r。长梗蘑菇是非常有用的蘑菇。种植它们让我们可以利用它们做很多事。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9808, 'zhCN', '你找到白闪菇了吗，$N?它们真的在沼泽里到处都是。$B$B如果你注意的话，远远就可以看见它们闪亮的白光了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9809, 'zhCN', '如果你有更多白闪菇就给我。我们村子永远都需要更多白闪菇。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9810, 'zhCN', '大地之环会尽全力帮忙。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9811, 'zhCN', '你想和我说话，$C?我可不记得你有先预约喔。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9812, 'zhCN', '我得知有位奎尔萨拉斯的客人要来，我期望是个血精灵，来告诉你真相。$b$b你带来什么消息吗？', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9813, 'zhCN', '是吧, $C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9814, 'zhCN', '你弄到蘑菇了吗，老兄?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9815, 'zhCN', '不要留下任何受污染的菌体!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9816, 'zhCN', '<巫医托卡斯咧嘴笑了笑。>$B$B失落者喜欢他们的礼物吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9817, 'zhCN', '你是来领取纳吉斯.法亚司的赏金吗?这是我今天听到最好的消息了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9819, 'zhCN', '他们沉睡了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9820, 'zhCN', '你拿着什么，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9821, 'zhCN', '戈达乌吃着这块石头。戈达乌找到“侵入者”。把石头带来!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9822, 'zhCN', '你找到巨魔意图的证据了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9823, 'zhCN', '安格拉斯威胁不断向我们逼近。你消灭了他们的领导者吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9824, 'zhCN', '读数，$N。你完成了吗?', 0);
