INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (9854, 'zhCN', '明白自己在大自然中的位置能带给你一种平静，你了解那种平静吗?你是猎人还是猎物?', 0);
