INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10099, 'zhCN', '我几乎不敢问。看到你安好的从矿坑里走出来代表着有两种可能:要不就是你杀了那怪物，要不就是你失去了你的胆量。那么，是哪一个呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10100, 'zhCN', '你有办法从这所有线索中找到这个罪人吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10101, 'zhCN', '<库尔的手脚随着身体的光源振动同时的移动起伏。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10102, 'zhCN', '<阿达欧的身体旋转和律动如同舒服的声音充满你的心里。>$B$B我们没有办法去解决玛格汉的困境。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10103, 'zhCN', '你是从猎鹰哨站来的援军吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10104, 'zhCN', '我不认为我之前曾在这见过你，$C。我可以怎么帮你呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10105, 'zhCN', '你从祖瑞伊那带来了什么消息吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10106, 'zhCN', '$N，等你协助完每一个堡垒的占领行动后回来找我!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10108, 'zhCN', '说吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10109, 'zhCN', '拿到气体了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10110, 'zhCN', '在你协助占领完每一个堡垒后回来找我吧!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10111, 'zhCN', '我有跟你说我对蛋的爱恋程度吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10112, 'zhCN', '嗯，我的记忆有增进吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10113, 'zhCN', '我就知道他是最没用的那一个。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10114, 'zhCN', '你找到我兄弟吗?真棒的消息啊!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10115, 'zhCN', '你有看到这些匕首沼地者做了什么吗?现在你了解为什么我们必须将他们与其它失落者隔离了吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10116, 'zhCN', '你想要领取姆玛奇酋长的奖赏吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10117, 'zhCN', '你想要领取姆玛奇酋长的奖赏吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10118, 'zhCN', '匕首沼地者唯一了解的事就是快速又残暴的攻击。他们应该知道我们不会让他们任意妄为。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10119, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10120, 'zhCN', '我看到你在跟欧莱恩谈话 - 一定是很重要的事吧……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10122, 'zhCN', '伏倒，你要在被抓到前快去检查这些计划。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10123, 'zhCN', '只要秘法师和他的血法师们持续在前线，萨尔玛就安全了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10128, 'zhCN', '感谢圣光你找到我了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10129, 'zhCN', '假如你在第一次操作的时候失败了，这里是第二个炸弹……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10130, 'zhCN', '你看起来很不安。有什么发现吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10132, 'zhCN', '你已经消灭了这个巨大的威胁了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10134, 'zhCN', '你手中的水晶还真大。那是你到山脊杀死戈罗西时得到的吗?$B$B让我看看。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10136, 'zhCN', '命运来的时候谁都挡不住，$N。残忍的阿拉萨尔斯已经逃避他的命运够久了。把他的死讯告诉那些胆敢反抗部落之人。而你就是下一个。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10138, 'zhCN', '我们离目标愈来愈近了，$N。你发现了什么吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10139, 'zhCN', '自从你离开后变得安静多了，$N。你有什么事要报告的吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10140, 'zhCN', '感谢圣光你安全的将它送达这里。荣耀堡需要集合所有经验丰富的战士。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10141, 'zhCN', '<阿图玛斯中士没有注意到你。她看起来有点疲惫。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10142, 'zhCN', '只要弓箭手看守着我们的领空，荣耀堡就会很安全。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10143, 'zhCN', '所以你就是清除苦痛之路的那个人，是吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10144, 'zhCN', '真希望我能到那里观赏烟火展示。那些爆裂物的威力强大!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10146, 'zhCN', '这是个非常重要的任务，$N。是个我不会轻易相信随便任何人的任务。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10150, 'zhCN', '俯倒，在你还没被抓到前快去检查这些计划书。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10152, 'zhCN', '你看起来很不安。你发现了什么吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10156, 'zhCN', '我们离来源愈来愈近了，$N。你发现什么了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10157, 'zhCN', '斥候马克哈在从葛卡兹哨站回来时带回了有关你的发现的消息。你在这对抗邪兽人的事迹不会被忽略的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10159, 'zhCN', '你去清理棘牙丘陵的这件事进展得如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10160, 'zhCN', '很开心可以在这看到新面孔。你是军队指挥者派来的吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10161, 'zhCN', '你找到足够的零件了吗?如果没有，我可能得使用那个雷噶西一直在附近挥舞的平底锅。接着，我必须将飞艇重新命名为＂飞行平底锅″。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10162, 'zhCN', '让他们知道不只有领空是安全的!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10163, 'zhCN', '让他们知道不只有领空是安全的!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10164, 'zhCN', '拯救那些灵魂，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10165, 'zhCN', '我不喜欢『分享』。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10166, 'zhCN', '你回来了，$N。你做得还不够吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10167, 'zhCN', '<迪欧瑞的鬼魂哼了几下。>$B$B你好啊，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10168, 'zhCN', '<族母卡修尔看起来很生气。>$B$B这些纳鲁远比世界上所有形态的生命还要聪明。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10169, 'zhCN', '这些药草跟香料的香味充满着锅炉周围的空气中。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10170, 'zhCN', '我看到你的梦境，$N。灵魂看顾着你……以他们无形的碰触指示你……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10171, 'zhCN', '话语……无意义的话语……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10172, 'zhCN', '<主母吉雅躺了下来。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10173, 'zhCN', '直到大法师瓦戈斯之杖归还我才会休息。你可以从恶魔的手中将它抢回来吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10175, 'zhCN', '<萨尔从他的宝座站起来迎接你。>$B$B他们说你从外外域带来紧急的消息，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10176, 'zhCN', '终于，我们见面了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10180, 'zhCN', '你怎么会知道在这里可以找到我?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10182, 'zhCN', '你找到什么了，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10183, 'zhCN', '忙阿忙阿。没有时间可以浪费了!有一艘火箭船等着施工呢!$B$B你要干嘛?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10184, 'zhCN', '它让我痛到了解能为我们的同胞做的太少了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10185, 'zhCN', '你有设法解决一些村庄废墟里的法力生物吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10186, 'zhCN', 'X-52虚空火箭看起来像是准备好可以发射了吗?我是为了我驾驶座的舒适而漫行虚空的吗?开发陌生的新世界?寻找新科技及商机?勇敢的去那些没有哥布尔去过的地方?$B$B不是的!$B$B因此，我希望你能有我给你之后的那些水晶。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10188, 'zhCN', '如果我要解开这座塔的诅咒，我就必须有那个符印。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10189, 'zhCN', '你拿到我要你去拿的东西了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10190, 'zhCN', '你已经将电池充满电了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10191, 'zhCN', '这么快就回来了?$B$B马克斯A型机器人五号还好吗?!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10192, 'zhCN', '你把书带来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10193, 'zhCN', '进行的怎样，$N?有任何消息吗?你最近为我做了什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10194, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10195, 'zhCN', '自从你带了第一批羽毛让我完成伯特泰欧的帽子，其它所有的佣兵吵着他们也要，包括萨德尔。我告诉他们我已经在进行了，但我需要替这个计划找到一个提供阿拉卡羽毛的稳定货源。$B$B我会很乐意接受你在森林探险中收集的任何阿拉卡羽毛。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10196, 'zhCN', '我很成功的引来了阿拉卡的灵魂到我身边，但他们仍然很害羞。也许如果我改变我的打扮，他们会更愿意在催眠状态下接近我。$B$B虽然，为了这样，我需要更多像你带来给我的这种羽毛。如果你有更多这种羽毛的话，我会很乐意从你手中接过来的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10197, 'zhCN', '你有拿到我要的那些东西吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10198, 'zhCN', '窃听的如何阿，$N?你最好快一点。我已经开始坐立不安了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10199, 'zhCN', '如果我能从这些虚空鳍刺萃取出足够的毒液，我想我们会拥有有史以来最具爆炸性的燃料!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10200, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10201, 'zhCN', '那么，你有测试的结果了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10202, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10203, 'zhCN', '这地方是不是很棒阿?而且每天都生产出崭新且奇特的东西!$B$B真是幸运，我们差点在探钻下来时毁了这座城镇。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10204, 'zhCN', '你调查过日怒博学者使用的那些血宝石了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10205, 'zhCN', '结束了吗?星移劫掠者尼萨德死了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10206, 'zhCN', '这么快就带着他们的技术回来罗?你最好不要只是在这扯我的后腿，$C。$B$B这里的X-52计划成功与否，就全靠你赶快将那些东西带来给我们了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10208, 'zhCN', '军团会对你带给他们的惊喜措手不及，对吧 $N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10209, 'zhCN', '你有安全的取得葛拉希厄斯之石吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10210, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10211, 'zhCN', '人的故事是短暂的，$N。而城市的故事才能长远流传。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10212, 'zhCN', '他来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10213, 'zhCN', '祝你在搜寻幸存者时拥有好运气。我怀疑是不是有人在这场空难中存活下来。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10214, 'zhCN', '唔，你在矿坑那里有任何进展吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10216, 'zhCN', '这个装备有上百个按钮，没有一个你看的懂得。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10218, 'zhCN', '哈罗，小噬尸兽。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10220, 'zhCN', '你让那些不屈的灵魂见识到反抗被遗忘者的下场了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10221, 'zhCN', '你将我们从布姆博士的蹂躏中解救出来了吗?我发誓现在将不会有任何爆炸性的武器了，虽然那非常的有趣!$B$B我们真的应该要常常那样做。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10222, 'zhCN', '完成了吗，$N!你已经为了肯瑞瓦村庄的沦陷复仇了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10223, 'zhCN', '你完成任务了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10224, 'zhCN', '你拿到所有精华了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10225, 'zhCN', '嗯，嗨。我现在有点忙。你有什么东西要给我吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10226, 'zhCN', '你从那些元素生物身上得到力量了没?喔，我不是想催你或什么的。只不过我的生活及工作都靠你了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10227, 'zhCN', '<蓝姆多惊讶的看着你。>$B$B嗯嗯，有事吗???', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10228, 'zhCN', '圣光与你同在，我的朋友。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10229, 'zhCN', '这是什么?一本传达不屈者意念的书?真是奇怪。我研究看看。也许我有办法知道不屈者的历史。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10230, 'zhCN', '我们得尽快帮助不屈者。要是我们能够说服他们加入我们，部落将获得一个有利的盟友。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10231, 'zhCN', '你要啥?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10232, 'zhCN', '我当然希望你忙着与燃烧军团战斗。$B$B没有什么是比背信更让我厌恶的，还有<咳嗽>地精，而恶魔必定就是这两种其中一种!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10233, 'zhCN', '烟从日怒要塞的堡垒中升起。那是你的杰作吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10234, 'zhCN', '你取得那些零件了吗?随着燃烧军团每次一个一个进驻我们在这里的领地，我们所剩的时间不多了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10235, 'zhCN', '解决末日裂爪了没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10236, 'zhCN', '你帮我的伐木机找到修理的零件了吗?我希望赶快修好他我才能继续去矿坑工作。不论如何我都必须要赚钱过日子，只有这样我才能赚足够的钱买一张回去藏宝海湾的单程票。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10238, 'zhCN', '你救回我的苦工了没?拜托快一点，我怕他们很快就会被吃掉而且他们应该不好吃。假如邪兽人吃了倒胃口的话他们可是会迁怒到我呢。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10239, 'zhCN', '你有办法取得到任何他们的能量来源吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10240, 'zhCN', '你启动了所有的符文了吗?我很急切的想知道是否我们无法改善这里的血精灵秩序。任何凯尔萨斯的手下可以做到的事，真正的肯瑞托可以做的更好。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10241, 'zhCN', '你完成任务了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10242, 'zhCN', '欢迎来到断脊氏族岗哨。我能帮你什么忙吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10243, 'zhCN', '这个在你面前出现的符号看起来怪异且难懂。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10245, 'zhCN', '让我们看看你带了什么要给我们，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10246, 'zhCN', '完成你的任务了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10248, 'zhCN', '你觉得如何?是否需要大翻修呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10250, 'zhCN', '你在攻塔时吹了这个号角了吗?我们必须快点进行这件事。谁知道萨尔玛何时会需要不屈者。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10251, 'zhCN', '你……你在找亡者之书吗?它不见了。我弄丢了……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10252, 'zhCN', '勒威萨斯的军队每分每秒都在茁壮。你一定要加紧脚步。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10253, 'zhCN', '你是谁，你想要干嘛?喔……是你喔。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10254, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10255, 'zhCN', '你知道实验的结果了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10257, 'zhCN', '你拿到拱心石了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10258, 'zhCN', '你是谁还有你想要从这个被诅咒的地方得到什么?这里不欢迎活人。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10259, 'zhCN', '再次进入裂缝里吧，我的朋友!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10261, 'zhCN', '我希望你是来关切我张贴的悬赏布告!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10262, 'zhCN', '完成了吗?你有除去外域的萨希斯渣滓，并将他们的徽记带来给我了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10265, 'zhCN', '我有印象王子正在寻找一颗特别的水晶，但是我不确定它是什么。$B$B你已经从惊惧领主那拿到一个了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10267, 'zhCN', '你拿到那些血精灵拖欠的调查设备了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10268, 'zhCN', '靠近。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10269, 'zhCN', '你好啊，旅人。你到海辛这来是想要买我的商品吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10270, 'zhCN', '你准备好现在谈生意了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10271, 'zhCN', '我相信你会有很多问题，所有的问题都会在适当的时间得到回答。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10272, 'zhCN', '你可以去收集蛋吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10273, 'zhCN', '这个玛利德是个笨蛋，或者我该说，曾经是个笨蛋。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10274, 'zhCN', '你去挑战并且打败维拉库了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10275, 'zhCN', '你要买还是要卖?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10276, 'zhCN', '又见面啦，我的朋友。水晶找的如何阿?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10277, 'zhCN', '直到你至少初步了解到发生的事情前都不要回来。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10278, 'zhCN', '你有试着使用我的实验设备吗?我认为我的不稳定的扭曲裂隙生成器会在如此接近坑道的深处的地方发生作用。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10279, 'zhCN', '你好……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10280, 'zhCN', '<纳鲁说话的美妙口音充满了你的内心。>$B$B我感觉到你带着某种拥有巨大力量的东西，$r。你带给我的是什么呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10281, 'zhCN', '我很开心你决定接受我的邀请。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10282, 'zhCN', '不用紧张，凡人。你的外表已经被改变了，因此你不会干扰到时间线。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10283, 'zhCN', '你是谁?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10284, 'zhCN', '<伊洛森点了点头。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10285, 'zhCN', '这么快就回来啦?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10286, 'zhCN', '你取得我想要的资讯没，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10287, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10288, 'zhCN', '我看过你在那跟杜朗交谈。你一定是在进行一项重要的任务吧!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10289, 'zhCN', '来自欧莱恩的报告吗?快一点拿过来!$B$B<克洛克尔克将军皱着眉头迅速地审视了潦草的文件。>$B$B可恶，这是我们所担心的。欧莱恩和他的战士们随时可能被包围!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10290, 'zhCN', '我了解收集法拉莱特的风险，但是做生意也是如此。不愿意冒险的商人最好关门大吉，为自己减少麻烦。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10292, 'zhCN', '你有拿到那些我需要的火炮吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10293, 'zhCN', '你仍然活着，表示你已取得核心，或者你已经改变主意。是哪一个呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10294, 'zhCN', '你已经完成了吗?你拿到我们需要的裂片了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10295, 'zhCN', '你解决虚无男爵.加伦希斯了没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10297, 'zhCN', '你在每一个时光之袋只可以使用一个时间信号灯。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10299, 'zhCN', '你已经完成你的任务了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10300, 'zhCN', '你已经设法找到适合当作大法师法杖头部配件的水晶了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10301, 'zhCN', '你拿到紫罗兰之目了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10302, 'zhCN', '我正在进行我认为对这些奇怪生物有效的解决办法。我只是需要更多的时间。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10303, 'zhCN', '他们的人数太多了……我们必须削弱他们，否则那些幸存者将没有机会对抗他们。', 0);
