INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10308, 'zhCN', '联合团对于彻底消灭所有在南方聚集之地的萨希斯反叛者感到强烈的兴趣。$B$B$C，如果你想让我们更加喜欢你，就回去那里带回更多他们的联合徽记。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10309, 'zhCN', '我的视力一定是随虚空辐射线而去了。你取得恶魔劫夺者之心了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10310, 'zhCN', '我可以帮你什么吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10312, 'zhCN', '你找得到记录簿吗?', 0);
