INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10319, 'zhCN', '在我们能攻击他之前，你必须取得纳伯瑞厄斯的骨匣。否则，他会是刀枪不入的。', 0);
