INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10324, 'zhCN', '这些雄鹿的皮革是坚固而表面布满粗线条的图案，他们的肉非常的坚韧 - 几乎不能吃，但是我们有其它的用途。', 0);
