INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10326, 'zhCN', '你带回了更多敌人的印记了吗，$C?燃烧军团仍是一个威胁，不论天涯海角我们都将继续对抗他们。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10327, 'zhCN', '我将拿走你剩下的所有印记。别担心;你的行为将被正式的表扬。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10328, 'zhCN', '你去法力熔炉杜罗取得简报的进展如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10329, 'zhCN', '计划成功了吗?老实跟你说我预计有一半的机会你会一起被炸死。', 0);
