INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10331, 'zhCN', '你找我的锤子找的还顺利吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10332, 'zhCN', '<眼泪在这个徒弟开口时冒上了他的眼眶。>$B$B你已经……解决朗瑟士大师了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10334, 'zhCN', '<这只牛站在本来的地方，怀疑地看着你。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10335, 'zhCN', '动作真快。你一定是一个非常有效率的人，已经将那3个标记全部放置妥当。', 0);
