INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10346, 'zhCN', '在这个深渊沙洲上仍然有许多的目标，$N。当你准备好进行下一回合时找只狮鹫兽吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10347, 'zhCN', '军团一定不认为他们在这深渊沙洲的高顶上是安全的。我们一定要不断的攻击他们!', 0);
