INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10382, 'zhCN', '没有必要站在这盯着我美丽的脸!退下!$B$B<莱弗卡尔狂笑>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10384, 'zhCN', '你拿到数据机体了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10385, 'zhCN', '你拿到传达资料了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10389, 'zhCN', '嗯，$C - 你打败恐惧恶魔了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10390, 'zhCN', '快点，$C，我们得在这些恶魔建造完成前让黑色祭坛停工!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10391, 'zhCN', '军团随时都有可能对我们发射，$N!你得去毁掉那些该死的火炮!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10392, 'zhCN', '快点，$N。传送门每开一秒，就多一个邪恶的恶魔被带到这个世界里!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10393, 'zhCN', '我没有太多时间可以浪费，$C。这就是让你要烦我的东西吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10394, 'zhCN', '不要告诉我你害怕了，$C。要是他们完成了炼治场的兴建，你就有比那些恶魔劫夺者更多需要担心的事!去吧!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10395, 'zhCN', '这是什么，$C?我感觉你找到的东西说明了一场即将到来的灾难。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10396, 'zhCN', '军团随时都有可能开始炮轰部落，$N!你得摧毁那些该死的火炮!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10397, 'zhCN', '你关闭传送门了没，$N?当你在这拖拖拉拉的同时，就有更多的恶魔进入了外域!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10399, 'zhCN', '我感觉到你心中一股日渐增长的黑暗，$C。你正视过你的恐惧吗?或是它还在阿葛纳尔之池那等待着你呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10400, 'zhCN', '告诉我，$N，任务完成了吗?军团领主被制伏了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10403, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10404, 'zhCN', '你成功的抵抗军团了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10405, 'zhCN', '你拿到裹带了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10406, 'zhCN', '他一定得成功!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10407, 'zhCN', '你拿到索奎萨尔的传送石了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10408, 'zhCN', '他……他真的死了吗?被杀了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10409, 'zhCN', '愿圣光与你同在，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10410, 'zhCN', '什么困扰着你呢，孩子?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10411, 'zhCN', '淤泥怪和荒废者都带有极毒。如果不予理会的话，他们将形成凝结的虚无恐兽!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10412, 'zhCN', '继续，$N。请说。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10413, 'zhCN', '哇，你看起来有点脏……你是掉进什么地方里面?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10414, 'zhCN', '即使是最小的贡献也会被注意到，$N。我们的敌人很多，但是我们将战胜他们!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10415, 'zhCN', '你最近为我们做了什么，$N?对凯尔萨斯的战争是不会自己开始的，你知道吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10416, 'zhCN', '你拿到秘法宝典了没，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10417, 'zhCN', '你取得诊断结果了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10418, 'zhCN', '你释放了那些动物的生命了吗?不要误解我，我真的是一个动物爱好者，不过不是当动物们把给予它们食物和庇护的人的手给咬掉的时候。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10419, 'zhCN', '你拿到更多的秘法宝典了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10420, 'zhCN', '你有拿到任何魔化武器没，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10421, 'zhCN', '你今天有拿到任何魔化武器吗?让圣光消灭他们，$N。别让哪些污秽的生物残存在这世界上。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10422, 'zhCN', '你找到泰拉里斯了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10424, 'zhCN', '你取得那些诊断了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10425, 'zhCN', '嗯?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10426, 'zhCN', '你有试着去集中秘境的能量吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10427, 'zhCN', '你替足够的塔巴克贴上标示了吗?', 0);
