INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10444, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10445, 'zhCN', '我要求你做的事几乎是不可能办到的，但我仍然希望你可以成功。全靠你了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10446, 'zhCN', '莫瑞狄恩中尉一直都在通报我们有关火翼岗哨的状况。一切都还好吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10447, 'zhCN', '丘尼士官一直都在通报我们有关火翼岗哨的状况。一切都还好吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10448, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10449, 'zhCN', '为部落效劳是我的荣幸。我能帮你什么忙?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10450, 'zhCN', '你挑战了噬骨者吗，$N。我希望他们的鲜血洒满大地!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10451, 'zhCN', '你找到威尔达姐妹了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10455, 'zhCN', '我没有说明真相？ 不论是对过路者还是希尔瓦纳本身，林地山猫都已经成为一个威胁。$B$BTheir deaths are regrettable, but the whole point of our creation of the Living Grove is so that Sylvanaar can be surrounded by a defensive region of natural beauty and tranquility.', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10456, 'zhCN', '我无法充分的强调杀掉那些雷霆王恐狼的必要性。他们的存在危及到生命之林的稳定以及我们在这的工作。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10457, 'zhCN', 'How fares the bolstering of the Living Grove\'s defenses?$B$BWith the intentions of the arakkoa at Veil Lessh unknown, and the Horde just on the other side of the ravine, I fear that if we do not move quickly we will find ourselves in a poor situation to protect our new home and our work in the grove.', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10458, 'zhCN', '我担心这个大陆可能失去希望了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10459, 'zhCN', '你完成我要求你的任务了吗，$N?', 0);
