INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10480, 'zhCN', '你在找到纳迦的地方就差不多可以找到那些受折磨的水之灵。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10481, 'zhCN', '很快的，我会将可与灵魂对话。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10482, 'zhCN', '噬骨者大量出现，$N。杀了他们否则邪兽人会侵扰我们!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10483, 'zhCN', '从荣耀堡回报，$N?很好 - 我们对抗恶魔和邪兽人的战役已越来越缺少健全的人手了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10484, 'zhCN', '你弄到护符了吗，$N?知道那些被诅咒的兽人拥有我们士兵的徽记真令我心痛……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10485, 'zhCN', '莫克的破碎胸甲在哪?你再次勇闯萨斯葛尔了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10486, 'zhCN', 'Our survival here depends upon our ability to concentrate our efforts against those that truly matter, not the mere bladewing bloodletters.$B$BYou are here again, so this must mean that you have dealt with the matter at hand?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10487, 'zhCN', '看来联盟似乎决定一路跟着我们不管到哪。让我们教教他们这样子没办法吓倒我们。$B$B你有带来我要的东西来吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10488, 'zhCN', 'How fares the bolstering of our Thunderlord dire wolve\'s defenses?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10489, 'zhCN', '你拿的那个是什么?你真是个勇敢的家伙!', 0);
