INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10785, 'zhCN', '你有在戈隆的大袋里找到沙伯迈恩所需要的吗?他给了我们什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10786, 'zhCN', '我们在短时间内造就了许多，你和我。我敢说你是我所知道之最有能力的巨魔猎人。$B$B我相信你不久后将会对付圆面之地的巨魔们吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10788, 'zhCN', '怎么了，年轻的$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10789, 'zhCN', '怎么了?你注意到召唤魔法的呼唤了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10790, 'zhCN', '怎么了，小家伙?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10791, 'zhCN', '与狼魂重建联系正是我旅行的开始，而非结束。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10792, 'zhCN', '萨斯葛尔必须被烧毁，$N!当你的任务完成时，再次与我谈话。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10793, 'zhCN', '<阿瑞利恩将军漂浮在水晶中间。>$B$B你……你在这里做什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10795, 'zhCN', '我无法足够强烈地表示你尽快杀死多尔哥克有多重要。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10796, 'zhCN', '你摧毁了血槌营地了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10797, 'zhCN', '你好，$g少年:少女;，你带来什么消息?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10798, 'zhCN', '你是什么人?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10799, 'zhCN', '你弄到全部的地壳穿刺者毒腺了吗?如果不是，就离开这里不要浪费我的时间!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10800, 'zhCN', '你再次来到我面前，$C。这必定代表着你已经从格鲁洛克的鼻子下面取出袋子。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10801, 'zhCN', '男爵帮我们做了陷阱了吗?强大的足以杀死一只戈隆吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10802, 'zhCN', '讯息被递送了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10803, 'zhCN', '我敢说你是我所知道最有能力的巨魔猎人。$B$B我相信那时你很快就会对付圆面巨魔吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10804, 'zhCN', '我不能靠自己救出他们，$N!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10805, 'zhCN', '你设法消灭在格鲁尔的巢穴的刀塔食人魔了吗?$B$B不要给我理由，$g男孩:女孩;!完成这工作!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10806, 'zhCN', '不久之后，格鲁尔全部的儿子都将被消灭。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10807, 'zhCN', '你已经完成我交给你的任务了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10808, 'zhCN', '完成了吗，$N?你阻止黑暗议会完成他们的仪式了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10809, 'zhCN', '报告，$N。我在你袖子上看见的是血之谷的血吗?我希望是!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10810, 'zhCN', '别来烦我!你看不出来我正在解开神秘力量的秘密吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10812, 'zhCN', '我不敢相信我的眼睛。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10813, 'zhCN', '血之谷氏族的兽人很久以前是黑暗的盟友……甚至是比现在更黑暗的敌人。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10814, 'zhCN', '允许我告诉你我完整的故事。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10816, 'zhCN', '嘿，\n $N。你带了什么消息回来?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10817, 'zhCN', '嘿， $N。你带了什么消息回来?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10818, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10819, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10820, 'zhCN', '我已经失去耐性了，为什么那些人还没解决呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10821, 'zhCN', '你想被我加入可派去送死的士兵名单吗?拿到那些钥匙然后杀死末日哀嚎者!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10822, 'zhCN', '即使是最小的贡献也会被注意到，$N。我们的敌人很多，但是我们将战胜他们!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10823, 'zhCN', '你最近为我们做了什么，$N?对凯尔萨斯的战争是不会自己开始的，你知道吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10824, 'zhCN', '继续，$N。说吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10825, 'zhCN', '$C，你感觉到了吗?我感觉到附近有反常和邪恶的东西。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10826, 'zhCN', '你身上还有我们敌人的印记吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10827, 'zhCN', '你佩带了更多我们敌人的徽记了吗，$C?燃烧军团仍是一个威胁，我们将继续在任何我们找到他们的地方追捕他们。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10828, 'zhCN', '我愿意收下你身上任何多的印记。别担心;你的行为将受到适当的表扬。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10829, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10830, 'zhCN', '居住在树林里的邪恶魔物开始渗入树林里所有属于大自然的生物。$B$B你必须动作快一点，$N，在一切来不及挽回之前!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10831, 'zhCN', '你制作了原始月布样本了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10832, 'zhCN', '你取得虚空怨灵的样本了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10833, 'zhCN', '你有使用水晶的力量来加深你与暗影祭坛的融合吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10834, 'zhCN', '血之谷兽人的罪恶没有止境，而且只有他们的破碎之手亲戚可以和他们匹敌。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10835, 'zhCN', '祝你有美好的一天，$N。你在地狱火半岛过的如何?我相信是丰收的……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10836, 'zhCN', '你已经完成了吗?我只能希望一切不会太迟……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10837, 'zhCN', '我需要幽藤水晶。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10838, 'zhCN', '你弄到读数了吗?你的恶魔占卜器任务成功了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10839, 'zhCN', '黑暗围绕我们。你有什么关于迷雾斯奇司的事要说吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10840, 'zhCN', '你消灭了多少以太族?我希望那足够从圣光之墓中将他们驱赶出来。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10842, 'zhCN', '你已经救了我们。我们该怎么感谢你?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10843, 'zhCN', '摩克纳萨尔不接受失败，或者找理由。要不就是你消灭敌人，要不就是$g他:她;杀死你!$B$B既然你正站在我面前，你一定已经杀死了葛诺斯.布罗格纳。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10845, 'zhCN', '虽然在我眼里你不能更进一步，但是承认你无法胜任杀死美声山脊虚空龙之母的挑战并不羞耻。$B$B那是你要告诉我的吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10846, 'zhCN', '我们有很多敌人。有些是我们选择的，而有些，像是维克尼尔，却不是我们选择的。$B$B不管怎样，我们将杀死他们的所有首领，直到没有一个可以站在我们面前。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10847, 'zhCN', '你回来了，$C。你带来了什么消息?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10848, 'zhCN', '迷雾瑞汉兹的魔物日渐强大。消灭他们，$r。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10849, 'zhCN', '你长途跋涉替我带来了消息。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10850, 'zhCN', '你打开恶魔劫夺者倒入燃料的盖子。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10851, 'zhCN', '你已经去剑刃岗哨取走他们的图腾了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10852, 'zhCN', '我好无聊，没有人可以陪我玩。你找到我的朋友了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10853, 'zhCN', '美声山脊的那些龙也许对我们是一种威胁，但是我想他们是崇高的生物。$B$B或者说，至少他们曾经是如此。我希望你可以拯救他们的灵魂。$B$B假如你需要更多的图腾，我还有多的可以给你。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10854, 'zhCN', '别浪费时间，我们还有很多事情要做!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10855, 'zhCN', '你已经试过我的小实验了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10856, 'zhCN', '进行的如何?你有机会教训一下那些燃烧军团吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10857, 'zhCN', '你成功了吗?传送器已经被摧毁了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10859, 'zhCN', '你已经收集完毕所需的雷森光球了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10860, 'zhCN', '你已经找齐了所有的材料了吗?讲到这里我都快流口水了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10861, 'zhCN', '你完成你的命令了吗?在太迟之前毁掉恶魔!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10862, 'zhCN', '你想要什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10863, 'zhCN', '我感觉到纳鲁触碰到我，而且我看到了那道光芒。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10864, 'zhCN', '恶魔占卜器还没有缓和下来，$N。当你收集足够的破碎之手兽人灵魂就回来找我。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10865, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10866, 'zhCN', '<凯瑞纳古呻吟着。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10867, 'zhCN', '你完成了吗?我们祖先的灵魂已经安全的从邪恶的以太族手上取回了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10868, 'zhCN', '你杀了多少阿拉卡呢$N?他们有没有逃跑?他们有没有在你横冲直撞的杀进他们营地的时候吓得躲躲藏藏呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10869, 'zhCN', '你杀了多少阿拉卡呢$N?', 0);
