INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10873, 'zhCN', '你找到我们失踪的人了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10874, 'zhCN', '迷雾撒拉斯必须要被消灭!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10876, 'zhCN', '你已经挑战卡加斯之手了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10877, 'zhCN', '你已经取得恐怖圣物了吗?我们必须拥有它。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10878, 'zhCN', '你已经完成我的要求了吗，$N?我担心我们的生命安全。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10879, 'zhCN', '现在不是闲聊的时候，$N。我们已经被攻击了!阻止那些侵略者!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10880, 'zhCN', '你想给我看什么东西吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10881, 'zhCN', '你已经取得那些遗物了吗，$N?我们不能允许暗影议会使用它们的力量来从事邪恶的行为。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10882, 'zhCN', '<阿达欧将注意力放在你身上，他的凝视使你几乎无法忍受。>$B$B$N，也许你不了解你的任务的重要性?已经没有时间可以浪费了……召集你的队友一起进入禁魔监狱。$B$B结束预言者斯克瑞斯的生命吧。', 0);
