INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10893, 'zhCN', '她死了吗?$B$B$N，如果我们在这里有作战机会，我们需要你透过杀死德拉卡.长尾让他们乱成一团!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10894, 'zhCN', '我可以帮你什么?你是从永恒树林带消息来吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10895, 'zhCN', '时间紧急，$N。我们需要烧掉那些警卫塔!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10896, 'zhCN', '你消灭了最坏的侵扰了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10897, 'zhCN', '你确定要这么做吗，$N?专精于药水将使你无法专精于其它领域。', 0);
