INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10909, 'zhCN', '破碎之手兽人的邪恶灵魂正在逐渐枯萎可怜的乔渥灵魂。告诉我，$N……他的情况已经被缓和了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10910, 'zhCN', '我已经飞过头了，然后看到你进入。我很高兴你向我打了信号。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10911, 'zhCN', '你已经摧毁那两座星移门了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10912, 'zhCN', '事情已经解决了吗?死亡之门的危机已经解除了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10913, 'zhCN', '这个问题很难启齿，我知道……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10914, 'zhCN', '不要留活口!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10915, 'zhCN', '你必须在那个古老的祸害恢复它的力量之前摧毁它。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10916, 'zhCN', '你已经找到祈祷珠串了吗，$N?它们的法力对很多的神圣祭仪都很重要…包括驱邪的祭仪。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10917, 'zhCN', '你带着羽毛回来了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10918, 'zhCN', '司凯堤斯的手下仍然在不远的距离监视着我们这些流亡者。现在所有我们在沙塔斯城外的人都离死不远。$B$B帮我带回来更多的羽毛当作你战胜他们的证明。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10919, 'zhCN', '菲菲以期待的眼神看着你……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10920, 'zhCN', '我可以感觉到毒液在我的血液中流窜。纳鲁在呼唤我，我可以看见引领我的光了。你已经战胜那些惧牙寡妇了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10921, 'zhCN', '你已经杀死泰洛卡狼蛛了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10922, 'zhCN', '工作完成你就可以领钱了，$g小男孩:小女孩;。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10923, 'zhCN', '泰洛卡的受诅咒的泰利巴斯已经被净化了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10924, 'zhCN', '塞皮有乖乖合作和你一同取得我要的血吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10926, 'zhCN', '请快点，我们撑不了多久。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10927, 'zhCN', 'I swore I told Sentinel Moonwhisper to give direct orders. Are you not obeying them?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10928, 'zhCN', 'I swore I told Grunt Grahk to give direct orders. Are you not obeying them?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10929, 'zhCN', '我们认为那只从洞口跳出来的实际上是一只幼虫!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10930, 'zhCN', '带个队友一同前去吧!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10935, 'zhCN', '去找隐士巴瑞达，$N。假如想救回上校祭仪就必须成功。', 0);
