INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10946, 'zhCN', '命令已经达成了吗，$N?伊利丹不是一个有耐心的主人!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10947, 'zhCN', '你已经取得骨匣了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10948, 'zhCN', '', 0);
