INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10950, 'zhCN', '我们到了吗?保护我不受任何可怕的鬼魂攻击，好吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10951, 'zhCN', '这真是太太太刺激了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10952, 'zhCN', '你对黑暗之门了解多少?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10953, 'zhCN', '大地之环注视着你，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10954, 'zhCN', '是你带她来的吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10955, 'zhCN', '你怎么会来这里，年轻的德鲁伊?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10956, 'zhCN', '我一直在等你。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10957, 'zhCN', '什么事，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10959, 'zhCN', '什么事，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10960, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10961, 'zhCN', '你弄到准备药剂需要的所有材料了吗?请小心别把花粉洒在我的身上。这种花粉会让我不停的打喷嚏。$B$B<莫西斯嗅了一下材料然后打了个喷嚏。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10962, 'zhCN', '你认为他们所说的关于时光之穴的事都是真的吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10963, 'zhCN', '要是他们对于时光之穴的说法是真的，那么这里一定是一个强大力量之地。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10964, 'zhCN', '<你准备对沉睡中的德鲁伊使用药剂。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10965, 'zhCN', '快点，$r。如果这些怪物从墓穴中脱逃，将不只是克林塔梦境行者会有危险而已。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10966, 'zhCN', '很高兴又见到你了，$C。我相信你和年轻的朵娜一起共渡了一段美好的时光吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10967, 'zhCN', '很高兴又见到你了，$C。我相信你和年轻的瑟兰德雅一起共渡了一段美好的时光吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10968, 'zhCN', '很优秀。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10969, 'zhCN', '嗯?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10970, 'zhCN', '进展的如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10971, 'zhCN', '你找到身分标签了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10972, 'zhCN', '我需要更多的身分标签，$N。当前为止我发现这些标签不只是拿来辨识囚犯的身分而已，它同时也记载着位置!$B$B一旦我破解上面的编码，就可以明确的知道所有盟友被囚\n禁的位置，并且以最小的冲突救出他们。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10973, 'zhCN', '我知道你付出了很多，但是想想这样你可以拯救多少人啊!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10974, 'zhCN', '你办到了!有什么发现吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10975, 'zhCN', '我们当前陷入了僵局，$N。你收集到的资料已经给我们足够的资讯以救出我们被关在虚空风暴小监狱里头的盟友们，但是我们必须一股作气的把复仇军对巴什伊尔平台造成的威胁连根拔起。$B$B加入与复仇军的战斗吧!把复仇军监狱钥匙交给我，而我将帮你打造复仇军监狱钥匙，使你得以开启巴什伊尔平台静止密室。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10976, 'zhCN', '这将会是你最不愿面对的任务，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10977, 'zhCN', '假如你选择不再进行下去，我能谅解的，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10978, 'zhCN', '克林塔帮助你取得圣器了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10979, 'zhCN', '你帮莫西斯带消息来吗?他终于派人来找我了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10980, 'zhCN', '<亚索充满希望的看着你。>$B$B你找到任何关于乌鸦圣典的讯息吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10981, 'zhCN', '只有意志最坚定以及最勇猛善战的冒险者才会向我提出这项要求，$N。假如你想要面对法力陵墓第二间密室里头的惊恐魔物，我可以帮你制作通往那里的钥匙。给我一个节点之王印记，我就可以达成你的愿望。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10985, 'zhCN', '<希瑞对你打了招呼。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10986, 'zhCN', '长途跋涉而来的德鲁伊，我能帮上你什么忙?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10987, 'zhCN', '你已经抓到雀鹰了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10988, 'zhCN', '你收集的碎片够让我重组乌鸦圣典了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10990, 'zhCN', '<雀鹰似乎用亚索的声音说话。>$B$B你已经成功的释放战鹰灵魂的精华了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10991, 'zhCN', '<再一次地，亚索的声音由雀鹰发出。>$B$B你已经取得猎隼的精华了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10992, 'zhCN', '你已经战胜猎鹰守护者并取得它的精华了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10993, 'zhCN', '我一直在等亚索的消息。你们发现了什么呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10994, 'zhCN', '看起来你经过了重重考验才来到这里。寻找月亮石真的那么难吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10995, 'zhCN', '格鲁洛克已经死了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10996, 'zhCN', '你取得宝箱了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10997, 'zhCN', '你把史拉格的军旗带回来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (10998, 'zhCN', '你已经召唤邪恶的梵戈并且从他的手中取得魔典了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11000, 'zhCN', '你已经取得史库洛克的灵魂了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11001, 'zhCN', '你从塞泰克大厅带来了什么消息呢?你打败安祖了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11002, 'zhCN', '从有记忆开始，这是我第一次感受到了安静。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11003, 'zhCN', '从有记忆开始，这是我第一次感受到了安静。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11004, 'zhCN', '你已经取回暗影之尘了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11005, 'zhCN', '完成了吗，$N?要不是我受伤，我会亲自杀了那些该死的鸟人。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11006, 'zhCN', '看来指挥官并不是胡言乱语。交给我更多的暗影之尘，我就给你另一份药剂。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11007, 'zhCN', '你听见我的呼唤了吗?是的……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11008, 'zhCN', '你的工作完成了没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11009, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11010, 'zhCN', '你用加农炮炸掉他们的炮弹堆了吗？', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11011, 'zhCN', '你永远不知道乌鸦神何时会回来。从我们与他的短暂交战看来，他不会接受被击败的事实。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11012, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11013, 'zhCN', '<莫格咕哝着。>$B$B你迟到了……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11014, 'zhCN', '又来一个啊?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11015, 'zhCN', '虚空水晶有很多用处。主要是用来制造武器和护甲……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11016, 'zhCN', '我们拿镰夺怪皮革来制作护甲内里和建筑陷阱。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11017, 'zhCN', '像我说的，虚空之尘花粉是我们法术施放者使用的东西。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11018, 'zhCN', '在那里的贾格会用矿石来加强我们的武器和护甲。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11019, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11020, 'zhCN', '这是件肮脏的工作，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11021, 'zhCN', '你有东西要给我看吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11022, 'zhCN', '', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11023, 'zhCN', '轰炸任务进行的如何呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11024, 'zhCN', '解释一下!为什么你身上有着司凯堤斯的味道!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11025, 'zhCN', '当你了解那些裂片和水晶的本质与用途，你就能对底下的巨魔同胞们提供更大的帮助。$B$B那些裂片的名字来自于曾经居住在山顶的一个已经灭亡的古阿拉卡文明。$B$B你把那些裂片带来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11026, 'zhCN', '我们相信你已经使用驱逐水晶把整个燃烧军团的杂碎们送回去了?!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11027, 'zhCN', '那个营地的恶魔时常修理他们的传送门。贾克说$N这一次给那些恶魔好看!$B$B但是，贾克要另一个暗色符文来制作晶铸暗色符文给你。$B$B假如小$r没有暗色符文，你就去跟克朗克说话。他试着每天做一个暗色符文!也许你可以去他的袋子里拿一个?!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11029, 'zhCN', '那本书，$N。你弄到它了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11030, 'zhCN', '你弄到那个魔法药水了吗?那对小托卡司而言是全世界。', 0);
