INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11039, 'zhCN', '你是沙塔斯城派来的帮手吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11040, 'zhCN', '那个木箱看起来很像哥布尔工程大师的东西。是波西派你来的吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11041, 'zhCN', '谁敢接近莫格?最好是很重要的事。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11042, 'zhCN', '什么风把你吹来52区的呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11043, 'zhCN', '我能帮你什么忙?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11044, 'zhCN', '欢迎来到蛮锤要塞，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11045, 'zhCN', '你好，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11046, 'zhCN', '你好，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11048, 'zhCN', '你看起来很面生。你是来回报的吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11049, 'zhCN', '猎蛋行动进行的怎么样了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11050, 'zhCN', '我们不需要那些蛋落入不对的人手里，$N。把任何你找到多馀的蛋都交给我。$B$B只要记得行事低调避免引起怀疑!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11051, 'zhCN', '一个像你一样老练的专家在驱逐恶魔上应该不会有任何困难。你没有困难吧，$r?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11052, 'zhCN', '<阿达欧注意到你靠近，被一股温暖的圣光包围着。阿达欧似乎知道你有东西要交给他。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11054, 'zhCN', '那里有苦工要被惩罚，$N!动作快!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11055, 'zhCN', '关键全在于腕部的技巧，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11056, 'zhCN', '你回来了，$N。我才开始想着你会不会放弃我了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11057, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11058, 'zhCN', '你学会如何使用顶尖圣物了吗?我们和北边的沙塔尔天空卫队都对你所学到的东西很有兴趣。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11059, 'zhCN', '我们厌恶暴力，但至少我们派你去对抗的结构不是一个活的生命。$B$B说到这，你弄到那个头颅了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11060, 'zhCN', '恶魔正扩大建造炼冶场:愤怒。很快的，他们就会变的很强大并粉碎我们。你要先去毁了他们!$B$B夏图欧的传送门是关键，但它受到一种魔法护盾和一个叫剥蚀者的恶魔所保护。我们强大的术士制作了特殊的暗色符文让你能控制那个剥蚀者。$R给贾克暗色符文，我们给$r晶铸暗色符文。$B$B控制那个剥蚀者并尽可能支配从那个大门进出的恶魔来阻止他们!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11061, 'zhCN', '那个巫士的药水绝对会让我的儿子们变的更聪明，那对他们到了沙塔尔天空卫队之后要做的事会有很大的帮助。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11062, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11064, 'zhCN', '在老迈克乔年轻的时候，他是最厉害的……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11065, 'zhCN', '你捆绑好一些苍穹鳍刺了吗?你看起来还满会使用绳索的，所以应该花不了你太多的时间。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11066, 'zhCN', '你对我们贡献良多，$N。我要你知道我们真的很感谢你所做的一切，让我们有足够存量的最新坐骑!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11067, 'zhCN', '川普击败了我一些最厉害的骑手。别让他的污秽骗了你。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11068, 'zhCN', '如果你要赢这场比赛就不能轻忽克洛克。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11069, 'zhCN', '他或许看起来像个人类，但艾克曼和兽人待在一起的时间比大部分的兽人还要多!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11070, 'zhCN', '穆维里克是我见过最有天赋的骑手。唯一让他无法坐到顶尖兽人位置的问题，就是他的鲁莽。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11071, 'zhCN', '第二名不是那么糟，$N。你可以取代穆维里克在阵容里的位置……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11072, 'zhCN', '完成你的任务了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11073, 'zhCN', '完成了吗，$N?泰洛克被击败了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11074, 'zhCN', '我要每个泰洛克劲敌子嗣的象征物品。只有这样我们才能准备好召唤泰洛克。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11076, 'zhCN', '你收集好货品了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11077, 'zhCN', '镰夺怪侵扰的问题解决的怎么样了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11078, 'zhCN', '你的任务进展的如何，$N?我不需要告诉你这对我们继续在这附近飞行有多重要吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11079, 'zhCN', '你弄到皮鞭了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11080, 'zhCN', '你从顶尖圣物取得了放射增益吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11081, 'zhCN', '这是什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11082, 'zhCN', '怎么样?为什么那么久?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11083, 'zhCN', '问题“解决”了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11085, 'zhCN', '什么事，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11086, 'zhCN', '毕竟，是我们的领主把那些恶魔从外域放逐并关闭了他们的传送门。一切都应向伊利丹的辉煌鞠躬。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11089, 'zhCN', '你收集好制作灵魂火炮需要的材料了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11090, 'zhCN', '完成了吗?', 0);
