INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11100, 'zhCN', '<阿克斯点点头。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11101, 'zhCN', '你把他们粉碎在你脚下了吗，指挥官?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11102, 'zhCN', '你用加农炮炸掉他们的炮弹堆了吗？', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11103, 'zhCN', '你戒指里的力量是可塑的，虽然改变你的选择不容易。你确定你想替换它现在的形态吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11104, 'zhCN', '你决定违背先前的选择?我想我可以帮你改变它。最终达拉然在意你的忠诚比你选择如何表现它来的多。$B$B它索费不低。当然，我需要你把旧的戒指交回来。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11105, 'zhCN', '你戒指里的力量是可塑的，虽然改变你的选择不容易。你确定你想替换它现在的形态吗?', 0);
