INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11126, 'zhCN', '你把那些叛逃者间谍赶出塞拉摩了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11128, 'zhCN', '你拿到了那些剩下的宣传单了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11129, 'zhCN', '你找到凯尔了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11130, 'zhCN', '<霸德怀疑地打量着便条。>$B$B你拿着些什么，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11131, 'zhCN', '$N，你答应过会拯救那些孩子的!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11132, 'zhCN', '噢对，$N，我没叫错吧?$B$B你找到了我的地图吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11133, 'zhCN', '我想我听到了兵营传来的笑声。你把我给你的传单都发出去了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11134, 'zhCN', '你完成了对叛逃者基地的突袭任务吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11135, 'zhCN', '无头骑士走了吗?我们还没摆脱他的黑暗支配吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11136, 'zhCN', '很高兴见到你，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11137, 'zhCN', '你逮到他们的领袖了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11138, 'zhCN', '你好，$C。什么风把你吹到这里来?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11139, 'zhCN', '让我们看看你从迪菲亚那边‘借到’了些什么。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11140, 'zhCN', '你有找到任何与迪菲亚货物有关的残骸吗?', 0);
