INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11143, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11144, 'zhCN', '你有找到任何命令的线索吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11145, 'zhCN', '你释放了那些在黑蹄村的俘虏吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11146, 'zhCN', '你抓到了那些迅猛龙吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11147, 'zhCN', '你把迅猛龙放进了黑蹄村了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11148, 'zhCN', '你从黑蹄村拿到那些武装了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11149, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11150, 'zhCN', '你有好好利用那把火炬吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11151, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11152, 'zhCN', '你已经献上花圈了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11153, 'zhCN', '我们已经谈过了，对吧?这表示你完成了!你一定已经给那些腐烂的北海海贼一些颜色瞧瞧$B$B对吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11154, 'zhCN', '拜托告诉我你拿到蝙蝠粪便了?!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11155, 'zhCN', '这世间没有比锹牙腹侧的柔软嫩肉更奢侈的了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11156, 'zhCN', '关于阻止恐怖图腾进攻，你有任何进展吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11157, 'zhCN', '你进行得如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11158, 'zhCN', '<布洛葛满怀希望地看着你。>$B$B你带了血沼羽毛吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11159, 'zhCN', '布洛葛一定要知道，你有没有带给那些暴力的石槌之灵平静?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11160, 'zhCN', '你带了石槌旌旗吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11161, 'zhCN', '你拿到那些精华了吗?布洛葛一定要有那些东西才能替他的朋友复仇!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11162, 'zhCN', '布洛葛要进行他的复仇!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11163, 'zhCN', '<多娜四处张望了一下，然后开始对你低语。>$B$B$N，我接下来要说的，请务必保持机密，了解吗?$B$B我是被沙塔斯保安官派来渗透霸德的手下。我得承认，自从你来了之后，你是我在调查中最感兴趣的关键人物。$B$B但是你的行动已经排除了你的嫌疑。事实上，你杀死了疯狂的督军祖尔金，我已经强烈要求要奖励你的努力。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11164, 'zhCN', '快点，兄弟!$B$B如果我再不快点给那些长牙，他就要把我送进祖阿曼去试验他的面具。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11165, 'zhCN', '哇，兄弟 - 我得救了!你能来真是太好了，朋友。我才把我最后一个信号弹放出去!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11166, 'zhCN', '我相信我手制的伪装能够你在祖阿曼适当的保护。$B$B你替我勘查过那些区域了吗?还是我应该要开始找取代你的人?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11167, 'zhCN', '你回收了那些瓶子吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11168, 'zhCN', '你拿到了我要毒素腺体了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11169, 'zhCN', '你测试图腾的时候可有什么好运?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11170, 'zhCN', '你回来了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11171, 'zhCN', '我希望你会感激我给你的好机会，$N。$B$B你解决了老玛拉克雷斯没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11172, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11173, 'zhCN', '你取得毒液了吗?莫兰恩随时都可能发现它不见了……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11174, 'zhCN', '你对这些软泥怪的进展如何呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11177, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11178, 'zhCN', '<霸德试着在你面前掩饰他的不悦。>$B$B又是你…$B$B我不知道该说什么了 - 准备事项实在太忙了，我真的没空照顾你了。$B$B相信我，$N，这是一切都是为你好。或许，靠着一些奇迹，你可以靠自己成就一点名声。$B$B假如这真的发生，你就可以跟别人说当初是举世无双的霸德.奈德瑞克提拔的!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11180, 'zhCN', '你从在此出没的冤魂身上知道了些什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11181, 'zhCN', '你有替我找到任何草药吗?我们得加快动作，要赶在那种不知名的邪恶大举入侵控制这个地区之前。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11182, 'zhCN', '对付那些好管闲事的管理器的事情你进行得如何了，小家伙?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11183, 'zhCN', '结束了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11184, 'zhCN', '你看起来对自己信心满满。是来领那只怪物迅猛龙的赏金吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11185, 'zhCN', '你身上带着的是什么东西呢，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11186, 'zhCN', '你身上带着的是什么东西呢，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11188, 'zhCN', '真不幸，队长要你去激怒那些元龙。尽管他们欠缺一般我们熟知的龙族智慧，但他们依旧看起来像是种高贵的生物。更别提这带给我们的处境。$B$B我能斗胆询问你状况解决了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11190, 'zhCN', '我们炮弹的数量越来越少，$C。我们的敌人需要被重击，尤其是那些斯考恩!$B$B你拿到需要更多的炮弹了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11191, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11192, 'zhCN', '你取得那种蛇颈龙油了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11193, 'zhCN', '今天想买什么呢?婢鲈看起来相当新鲜。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11194, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11195, 'zhCN', '嗨，$N!我听说你要去加入霸德。你一定不会后悔这件事的，朋友，我可以保证。$B$B你那边有些什么，兄弟?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11196, 'zhCN', '哇，兄弟 - 我得救了!你能来真是太好了，朋友。我才把我最后一个信号弹放出去!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11198, 'zhCN', '回报你的情况。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11200, 'zhCN', '你在黑蹄村的搜索行动有什么发现吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11201, 'zhCN', '你有找到任何命令的线索吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11202, 'zhCN', '真的很难说明这里到底发生了什么事。你把那些瘟疫槽烧毁了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11203, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11204, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11205, 'zhCN', '你有好好利用那把火炬吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11206, 'zhCN', '你从塔贝萨身上打听到了什么消息呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11207, 'zhCN', '<莫克希看起来相当地担忧。>$B$B货物都还在吗?你有找到幸免的货物吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11208, 'zhCN', '嗯?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11209, 'zhCN', '记着，交易的条件是‘要是没东西咬你的话，就别回来。’', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11210, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11211, 'zhCN', '我能帮你什么忙?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11212, 'zhCN', '你好，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11213, 'zhCN', '嗨，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11214, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11215, 'zhCN', '我能帮你什么忙?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11216, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11217, 'zhCN', '你有找到龙尾草吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11218, 'zhCN', '在这里的建筑工事似乎是个没完没了的工作，我真希望你能拿到让我们能继续进行工作的物品。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11219, 'zhCN', '部落的孩子需要你!别抛下他们!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11220, 'zhCN', '无头骑士走了吗?我们还没摆脱他的黑暗支配吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11221, 'zhCN', '你要回来报告了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11222, 'zhCN', '你有什么事要说?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11223, 'zhCN', NULL, 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11224, 'zhCN', '这对我们来说真是一大打击。我甚至不想知道他们在那里到底发现了什么。$B$B光是用想象的，就足够让我一整晚睡不着觉了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11225, 'zhCN', '你在这里做什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11227, 'zhCN', '你喂饱了那个小恶棍吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11231, 'zhCN', '只要夏勒布隆的囚犯被释放，维库人就无法继续他们不洁的仪式。$B$B你是阻止他们的唯一希望。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11232, 'zhCN', '那些火炮，$N。有人要去搞定它们吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11233, 'zhCN', '那些联盟首领都死了吗，$N?除非他们都死了，不然不要回来。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11236, 'zhCN', '你确定你杀死了梅赞和他的死灵领主，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11237, 'zhCN', '什么?!攻击要塞的计划!$B$B一只冰霜巨龙?!!!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11238, 'zhCN', '阻止攻击了吗?冰霜巨龙和他的主人被摧毁了吗?$B$B你拿回号角了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11239, 'zhCN', '圣光经由你的行为散播出去，并进入所有的事物中。$B$B没有任何生物，没有任何地方，不管有多怪异，都无法可以逃过它的愤怒!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11242, 'zhCN', '你有东西要给我吗，善良的$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11243, 'zhCN', '世界的命运可能就掌握在我们手中。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11244, 'zhCN', '找到那些斥候!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11245, 'zhCN', '只要那些投掷器存在，太过靠近的下场就只有死。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11246, 'zhCN', '我很确定，我不想看到这些大块头在我们打倒之后又站起来!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11247, 'zhCN', '我有个建议，当我们放火烧掉这些建筑时，可以不要在里面待太久吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11249, 'zhCN', '嗯?我可以帮你什么吗，孩子?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11250, 'zhCN', '既然你人在这里，我想斯考恩已经不复存在对吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11252, 'zhCN', '$N，看到你还活着很让我高兴。$B$B我们的伤亡快要超过可以控制的范围了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11254, 'zhCN', '你有东西要给我看吗，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11255, 'zhCN', '有点毅力，$N!人命关天啊!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11257, 'zhCN', '维库人的传统真诡异，同意吧，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11258, 'zhCN', '我建议当建筑物起火以后，就不要待在里面了。火灵对破坏的渴望不会放过我们两个的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11259, 'zhCN', '只要那些施法者在上面，太靠近那些哨塔就是找死。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11260, 'zhCN', '嗯?我可以帮你什么吗，孩子?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11261, 'zhCN', '你带来了什么斯考恩的消息，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11262, 'zhCN', '做好准备，$r。$B$B还有很路复用作等着你完成。这只是冰山一角。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11263, 'zhCN', '夏勒布隆的战斗情形如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11265, 'zhCN', '只要夏勒布隆的囚犯被放走，维库人就无法继续他们的仪式了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11266, 'zhCN', '什么?!计划攻击我们的营地?$B$B凶手是一只天上的大巨龙?!!!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11267, 'zhCN', '阻止攻击了吗?这个巨大的死亡巨龙和他的主人被摧毁了吗?$B$B你拿回号角了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11268, 'zhCN', '你消灭了那些横行夏勒布隆的活尸了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11269, 'zhCN', '你有带任何羽绒来吗?我非常需要它…', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11270, 'zhCN', '我不在意之前你杀过多少的巨人，龙或是狗头人。去完成这份工作，并在完成后回来这里。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11271, 'zhCN', '你有收集到任何羽绒吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11272, 'zhCN', '很开心又看到你了，$N。除非你还没杀了那个精灵混蛋，如果我是你，在这样的情况下我就会赶紧出发了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11275, 'zhCN', '你带着合适的角来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11277, 'zhCN', '检查所有角落与缝隙!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11278, 'zhCN', '你回来了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11279, 'zhCN', '没有那些样本，我要如何分辨这个最新的瘟疫菌种是不是可以用于其它所有的物种身上呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11280, 'zhCN', '好啦，拿出来!我们的小实验有用吗?$B$B元龙喜欢它的点心吗?它有从体内开始溶解吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11281, 'zhCN', '你有机会测试号角吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11282, 'zhCN', '你杀死那几个维库人了?他们一定很难缠!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11283, 'zhCN', '你杀够了吗?温特斯考恩的城镇就在对面!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11284, 'zhCN', '你顺利地把那只笨雪人移出我的矿坑了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11285, 'zhCN', '你把所有的建筑都烧完了吗?确定你没有留下什么没烧掉。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11286, 'zhCN', '你从钢铁之门收集了多少神器?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11288, 'zhCN', '<爱力士失去意识。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11289, 'zhCN', '以圣光之名!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11290, 'zhCN', '你找到掠龙战斗计划了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11291, 'zhCN', '瓦尔加德没有沦陷?真是意外。$B$B一定是因为你的帮忙。肯定跟凯勒没有关系。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11292, 'zhCN', '希望你没有空手而回。我想我们之间有过协定了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11293, 'zhCN', '你完成了啤酒节的宣传了吗?更重要的是，你让大家知道麦酒了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11294, 'zhCN', '你让大家知道雷酒将在啤酒节登场吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11296, 'zhCN', '我抱着希望，盼望我的狩猎部队还活着。$B$B寡妇的刺毒并不会立刻致命。它们会让猎物活得够久，好让那些小蜘蛛孵化时可以大快朵颐。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11298, 'zhCN', '你拿到那些酒桶了没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11300, 'zhCN', '迪刚恩?密码?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11301, 'zhCN', '你拿到了那些脑子吗?如果没有，我要把我的锯子要回来。你最好不要想把锯子给带走。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11304, 'zhCN', '嗯，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11305, 'zhCN', '你拿到我需要的样本了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11306, 'zhCN', '你的实验进行的如何，$N?如果你有任何包含科学的问题的话，让我知道!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11309, 'zhCN', '你收集到我要的材料没有?', 0);
