INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11319, 'zhCN', '这个扭曲的林地是个可怕的地方。我从没想过我们的家会变成这么恶心的东西。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11321, 'zhCN', '怎么啦宝贝?有东西要给我?', 0);
