INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11327, 'zhCN', '那个地方看起真像是个噩梦。不管是不是维库人，没有人应该要沦为皇家药剂师学会的受害者。$B$B幸好我平安地站在这里。你拿到包裹了吗?', 0);
