INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (11469, 'zhCN', '油脂在哪里?别告诉我他身上什么都没有。$B$B大罗伊该死地胖成那样，他还会动真是个奇迹!', 0);
