INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12508, 'zhCN', '考虑再多杀一点吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12510, 'zhCN', '你拿到珍贵液体了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12512, 'zhCN', '你救回我们的精兵了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12513, 'zhCN', '那时候，我是无人可敌的…如此的骄傲，太骄傲了。从来没有任何挑战者赢得这顶精美的帽子……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12514, 'zhCN', '你取得所有的菌种了吗?$B$B喔，还有，小心潜伏在那里的蜥蜴。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12515, 'zhCN', '那时候，我是无人可敌的…如此的骄傲，太骄傲了。从来没有任何挑战者赢得这顶精美的帽子……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12516, 'zhCN', '结束了吗?$B$B调整过的魔精有效吗?$B$B你杀掉西莱图斯的预言者替他报仇了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12517, 'zhCN', '看来你弄到了整套的群盗套卡!令人印象深刻。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12518, 'zhCN', '看来你已集满全套的法师套卡!恭喜!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12519, 'zhCN', '你帮我弄到那些勋章了没，$r?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12520, 'zhCN', '你制服了犀牛吗，还是你丢毛巾投降了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12522, 'zhCN', '嗳，你有办法拿到引擎吗?我只知道资助远征队的大法师要紧张了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12523, 'zhCN', '你拿到零件了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12525, 'zhCN', '你解决了诈咧与他的保镖吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12527, 'zhCN', '这些是什么?$B$B你有看到我那个没路用的学徒，捣盆吗?$B$B我派他去办点事情，他搞了半天还不回来!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12529, 'zhCN', '你还活着!硬拳还没把你吃了?$B$B也许他们晚点会把你吃了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12530, 'zhCN', '你要做什么，奴隶?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12531, 'zhCN', '我并没有找你，$r。你要做什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12532, 'zhCN', '你抓到鸡了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12533, 'zhCN', '你学会狩猎了没有?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12534, 'zhCN', '你闻起来很像黄蜂之类的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12535, 'zhCN', '差不多准备好长途旅行了。杰克杰克刚刚带鳄鱼回来了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12536, 'zhCN', '他们跟赛匹克说不以狂心族人标准来看的话，你是个好猎人。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12537, 'zhCN', '赛匹克觉得有同伴一起顺利多了。以不是狂心族人来说你很强。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12538, 'zhCN', '我们是盆地最棒的猎人!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12541, 'zhCN', '看吧，$N，真正的炼金术远不只是仅仅混和几种材料……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12543, 'zhCN', '我们得知道 -- 你带牙齿来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12544, 'zhCN', '嗯，你询问过当地的神谕者了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12545, 'zhCN', '不要留下任何东西给巫妖王复活!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12546, 'zhCN', '威胁生命的家伙们…你已经解决他们了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12547, 'zhCN', '<伊提米迪安胸口的符文状凹槽似乎闪动着微弱的能量光芒。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12549, 'zhCN', '你在惧刃豹身上测试过你的技巧了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12550, 'zhCN', '跟踪进行的如何了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12551, 'zhCN', '你杀了足够数量了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12552, 'zhCN', '不，$C，你还没有完成任务。因为我知道科尔拉玛斯外围还有死灵魔导师在游荡。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12553, 'zhCN', '如果我要组装一个设备给你用的话，非得要用完整的丝囊不可。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12554, 'zhCN', '我期待着要把他的头挂在杆子上。让我们看看他这样还能不能复活!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12555, 'zhCN', '你确定你击坠够多了吗?我看起来像是很想要瘟疫的样子吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12556, 'zhCN', '你杀死那头野兽了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12557, 'zhCN', '你习惯我的实验室了吗，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12558, 'zhCN', '你解决尚戈了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12559, 'zhCN', '你回来了，小家伙。我都开始担心你的安危了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12560, 'zhCN', '你把我们需要的蕨叶带来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12561, 'zhCN', '不要浪费我的时间，凡人。泰坦的创作一定得好好守护!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12562, 'zhCN', '如果要我说的话，你应该再多对付几个达卡莱缚水者。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12563, 'zhCN', '有什么来自岗哨的消息吗，$r?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12564, 'zhCN', '你收集到水罂粟了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12565, 'zhCN', '<辛阿布瓦莫测高深地凝望着北方与南方。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12567, 'zhCN', '<辛阿布瓦石像的视线穿透了你的灵魂。$B$B你取悦了辛阿布瓦，但是他还想要你给他更多达卡莱祭品来表达你的感激与尊敬。$B$B他会好好奖励你的。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12569, 'zhCN', '我们的计划成功了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12570, 'zhCN', '你是谁?你闻起来跟小狗人一样臭。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12571, 'zhCN', '<高阶神谕者苏-楔的视线似乎直直穿过你。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12572, 'zhCN', '哈罗干皮人。你跟拉富相处的好吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12573, 'zhCN', '小狗人好好相处吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12575, 'zhCN', '笨小狗贼。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12576, 'zhCN', '他们总是想打架!我们不想打架，但我们要保护圣物。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12578, 'zhCN', '苏-楔有时候会犯错，但他并不笨。不过我确实认为他太过姑息了…小狗人不会停手也不会让我们好好过日子。阻止小狗人的唯一方法就是杀掉小狗人…没有“和平”这种事。$B$B<穆斗挺起胸膛指着他自己。>$B$B这我很擅长。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12579, 'zhCN', '苔行者都是好人…他们不该碰上这样的事情。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12580, 'zhCN', '我真希望我早一点知道柱子和石头之间的联系…也许我就能预防这一切。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12581, 'zhCN', '<骨匣不祥地留在阿楚依斯死去的形体旁边地上。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12583, 'zhCN', '你拿到零件了吗?嗯?拿到没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12584, 'zhCN', '嗯，$C?$B$B请告诉我你手上有我们请鲁伯特上尉去拿的萨钢矿石好吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12585, 'zhCN', '你拿到生火的木材了吗，$r?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12587, 'zhCN', '有什么来自岗哨的消息吗，$r?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12589, 'zhCN', '结果满意吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12592, 'zhCN', '要来算一算吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12596, 'zhCN', '你见过我的每一个上尉了吗，$r?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12597, 'zhCN', '你收集到水罂粟了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12598, 'zhCN', '你封闭了奈幽族的隧道了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12599, 'zhCN', '你拿到生火的木材了吗，$r?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12603, 'zhCN', '你测试了你的技巧吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12605, 'zhCN', '你弄到用来引诱育母的饵了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12606, 'zhCN', '你把我们的士兵从奈幽虫族的魔爪中解救出来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12607, 'zhCN', '事情都办好了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12609, 'zhCN', '你收集到我需要的药材了吗，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12610, 'zhCN', '你替菲肯斯坦收集完那些蝙蝠翅膀没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12611, 'zhCN', '完成了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12614, 'zhCN', '嗯，你还活着。你做的怎么样?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12615, 'zhCN', '<你感觉到繁荣之母的目光在看着你。她很好奇为什么一个像你这样的$r会站在她面前。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12616, 'zhCN', '你发现了什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12617, 'zhCN', '你摧毁那些入侵者了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12618, 'zhCN', '<辛托加不带感情地看着你。她怀疑你是否想藉由贡献更多达卡莱祭品来继续获得她的祝福。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12619, 'zhCN', '符文刃是你自身存在的扩展。一个死亡骑士作战时不能没有符文刃。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12620, 'zhCN', '你解决那些教徒没，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12621, 'zhCN', '我们时间有限。天灾军正在准备最后一次攻击。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12622, 'zhCN', '$C要和艾胡奈说话是因为$C拿到了所有的宝藏。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12624, 'zhCN', '戒指，$N!拜托…告诉我你已经找到了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12627, 'zhCN', '我还是没有办法创建联系。那些咒医的仪式还是很完整。$B$B请回去扰乱他们的每个锅子。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12629, 'zhCN', '你带来我需要的药材了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12630, 'zhCN', '靴子还不错吧，$r?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12632, 'zhCN', '还有很多我的子嗣身陷折磨之中，你必须解救他们!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12634, 'zhCN', '运气怎样，你有没有从那些又高又顽固的树上弄下水果来?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12635, 'zhCN', '达卡莱巨魔彻底忙着毁灭他们自己，如果让这些历史意义重大的东西像他们的帝国一样消逝的话真是太可惜了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12637, 'zhCN', '嗯，$r?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12638, 'zhCN', '嗯，$r?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12639, 'zhCN', '我希望你有带回足够的数量。金阿莱之池的攻势在你对他们做了那些事之后越来越凶恶。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12640, 'zhCN', '你得再封闭几个裂隙，然后我们再讲话，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12641, 'zhCN', '我们对血色十字军发动的突袭将会成为传奇。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12643, 'zhCN', '你带来我需要的药材了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12644, 'zhCN', '嗯，结果怎么样?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12645, 'zhCN', '不要让我一直等，$N。结果怎么样?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12646, 'zhCN', '快点，$r，你拿到我的魔精没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12647, 'zhCN', '如果你不尽快结束我的苦难，$N，我们将失去一切。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12648, 'zhCN', '嗯，$N?$B$B你买好那个瓶子没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12649, 'zhCN', '嗯，$N?$B$B你买好那个瓶子没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12650, 'zhCN', '我们是没有办法理解达卡莱巨魔的。他们是凶残的野蛮人，而且已经沉沦到了一个新低点。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12652, 'zhCN', '你还没喂食尸鬼?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12654, 'zhCN', '你找到匹奇了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12655, 'zhCN', '<你感觉到辛茹克并不相信你。他正打算要调用一个守卫。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12656, 'zhCN', '<辛茹克对你再次躲过他的守卫感到印象深刻又泄气。至少现在是这样。>$B$B<把祭品放在他面前，然后他就会再次赐予你他的祝福。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12658, 'zhCN', '你拿到那些蛋了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12659, 'zhCN', '艾胡奈知道$N绝不可能那么快就割完那么多希伯德拉卡巨魔头皮!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12660, 'zhCN', '完成了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12661, 'zhCN', '你得到指挥官的信任了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12662, 'zhCN', '他还没死。我从这里就听的见他在那里嚷嚷!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12663, 'zhCN', '你跟我将会拿下古达克，老兄。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12664, 'zhCN', '你上过战场没啊，老兄?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12665, 'zhCN', '是什么事让你耽搁了，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12666, 'zhCN', '我从未想过事情会变成这样。达卡莱巨魔已经彻底迷失了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12668, 'zhCN', '为什么在任务还没有完成的时候就回来?你难道还不了解我只要一个念头就能够杀死你?$B$B现在快滚，小$r。完成我所命令你去做的事!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12669, 'zhCN', '拜托，$N，告诉我你没搞砸了你的伪装……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12671, 'zhCN', '嗯，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12672, 'zhCN', '哈克娅没有早点派你来真是遗憾。但话说回来，她跟我也一直都处得不好。$B$B也许她故意拖延你的到来，故意让这一切发生。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12673, 'zhCN', '你有好好鞭打我的手下一顿吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12674, 'zhCN', '你以为我是个蠢蛋吗?$B$B他们还没有死!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12675, 'zhCN', '你回来了，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12676, 'zhCN', '你成功清除那些血尸车了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12677, 'zhCN', '欢迎回来，$N。$B$B你成功偷到达库鲁的水晶样本了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12678, 'zhCN', '这只不过是开场的奏鸣曲，死亡骑士。天灾军的死亡进行曲就从消灭前线开始。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12679, 'zhCN', '他们发射的射角应该会让大多数的箭枝落在南边的避风郡农场那一带。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12680, 'zhCN', '你不想一直徒步行走，对吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12681, 'zhCN', '你有找到任何的扭曲爪子吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12683, 'zhCN', '你拿到样本了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12684, 'zhCN', '我希望有什么方法可以让我们把玛姆托司带回这世界，但这种方法并不存在。$B$B我们只能持续对抗达卡莱巨魔，以荣耀他和他所做的牺牲。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12685, 'zhCN', '是什么把你带回来我这里，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12686, 'zhCN', '达穆克为他的失败付出代价了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12687, 'zhCN', '死亡骑士，你必须靠力量抢夺死亡战骑!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12690, 'zhCN', '你揍扁那些巨魔了没，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12691, 'zhCN', '这个石箱纹风不动。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12698, 'zhCN', '一点小提醒:矿工不是每次都会变成食尸鬼。有时候他们会变成一心一意想要杀了你的愤怒灵魂。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12699, 'zhCN', '你有找到任何东西吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12701, 'zhCN', '死亡骑士无所畏惧!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12702, 'zhCN', '通常鸡都不会跑太远…如果黄蜂不会吃掉他们的话，也许下次干脆就让它们呆在外面好了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12703, 'zhCN', '好多大舌头可以杀!好好玩啊，嗯?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12704, 'zhCN', '亮晶晶永远不够…伟大雨石对我们很好，我们也要对伟大雨石很好。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12705, 'zhCN', '谢谢干皮的帮忙。你对我们很好。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12706, 'zhCN', '当我派你出去的时候我很确信你不能活着回来，死亡骑士。也许你比我起初估计的更有潜力。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12707, 'zhCN', '什么?你已经完成了?$B$B你已经对那些可悲的寒冰巨魔展现毛茸茸的正义了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12708, 'zhCN', '想必你不可能那么快就解决了全部的附魔提基战士吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12709, 'zhCN', '也许在我们谈话的此刻你的包包里就装着储藏箱?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12710, 'zhCN', '你见识了我的作品没，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12712, 'zhCN', '你要不计代价取得督军的钥匙，我不能再更强调这件事的重要性了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12713, 'zhCN', '你在战斗中面对过达库鲁了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12716, 'zhCN', '你收集到我要的材料没有?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12717, 'zhCN', '瘟疫大锅一动也不动。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12718, 'zhCN', '瘟疫大锅不断的冒泡翻搅着有毒的液体，朝四周喷洒着浓浊的气体。$B$B你还有更多的十字军颅骨可以放进去吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12719, 'zhCN', '让他们无处可逃!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12720, 'zhCN', '对血色十字军使用说服者吧。他们之中总有人会…说溜嘴。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12721, 'zhCN', '发生了什么事?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12722, 'zhCN', '就像待宰的羔羊一样……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12724, 'zhCN', '撕裂那座堡垒吧!日程表就在那里!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12727, 'zhCN', '寇尔提拉怎么样了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12728, 'zhCN', '你从冬鳍洞窟收集好数据了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12729, 'zhCN', '你已经取得必要的物品了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12730, 'zhCN', '我只能够给你温暖的话语，以及言语中带来的勇气。$B$B磨利你的武器做好准备吧，但我恳求你，不要花太多的时间，否则预言者可能会直接袭击我们。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12732, 'zhCN', '你给瑞杰克带礼物来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12733, 'zhCN', '你完成死亡挑战了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12734, 'zhCN', '你让瑞杰克的剑刃喝血了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12735, 'zhCN', '你演奏净化之歌没，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12736, 'zhCN', '你觉得变聪明了吗，$r?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12739, 'zhCN', '你现在感觉如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12740, 'zhCN', '如果你有空回来跟我讲话，为什么我还会看到我的手下摔死呢?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12741, 'zhCN', '你把元素的真正力量带回来给瑞杰克了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12742, 'zhCN', '你现在感觉如何?', 0);
