INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12748, 'zhCN', '你现在感觉如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12749, 'zhCN', '你现在感觉如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12750, 'zhCN', '你现在感觉如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12754, 'zhCN', '你就要错过信差了，那样我就被迫得杀死你。说是被迫，意思就是说我会很高兴地了结你的性命。$B$B动作快!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12755, 'zhCN', '你上哪去了?你迟了整整两小时。我们正要派信差去找高阶指挥官。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12756, 'zhCN', '<欧贝兹笑了笑。>$B$B这是什么?某个小孩的绘画簿吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12757, 'zhCN', '你做了什么?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12758, 'zhCN', '你带了瑞杰克的新头盔来了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12759, 'zhCN', '真是一团乱啊!但还是很好玩。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12760, 'zhCN', '费克嘎教了你这个超级秘密，嗯?只有最强的狂心族人才能知道这个秘密。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12761, 'zhCN', '非常的强大。小狗人不晓得伟大诸神的力量。呆呆小狗人。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12762, 'zhCN', '有你在这里帮我们真好。好长的战斗。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12771, 'zhCN', '哈罗，$N。我从没看过这么结实的矮人!外域的试炼让你更强悍了，$g兄弟:姊妹;。你让你的族人很骄傲!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12773, 'zhCN', '$N，我的$g儿子:女儿;。我们又再一次被卷入一场可能会毁了一切我们所知事物的冲突中。幸亏有个$C愿意借出$g他:她;的能力来防卫世界树。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12774, 'zhCN', '$N，我很高兴看到你来这里。你的力量与技巧无人能敌，在这充满苦难的时刻你的援手非常重要。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12776, 'zhCN', '我的$g孩子:女儿;，你在兽人与人类的土地过得不错，这股力量在你身体中逐渐增强…', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12779, 'zhCN', '把他们全杀光……', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12781, 'zhCN', '我看到您手里拿着一张特殊的礼品券，把它交给我，您就可以得到一件特殊的礼品。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12785, 'zhCN', '$N，欢迎来到奥格瑞玛。大药剂师普崔司在你来以前先捎了口信过来…你手上有那颗受感染的脑子吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12786, 'zhCN', '$N，欢迎来到雷霆崖。大药剂师普崔司在你来以前先捎了口信过来…你手上有那颗受感染的脑子吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12787, 'zhCN', '向你致敬，$N。你从沙塔斯来的吧?你带来了大药剂师普崔司的指示吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12788, 'zhCN', '$N，欢迎来到银月城。大药剂师普崔司在你来以前先捎了口信过来…你手上有那颗受感染的脑子吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12790, 'zhCN', '我感觉到你还没测试那两颗传送水晶。$B$B没关系，慢慢来。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12791, 'zhCN', '你好，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12794, 'zhCN', '你好，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12796, 'zhCN', '你好，$C。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12797, 'zhCN', '你回来了，$N。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12798, 'zhCN', '看来你已集满全套的众剑套卡。太好了。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12799, 'zhCN', '你好，$r。$B$B你收集到菲肯斯坦要求的魂能了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12801, 'zhCN', '<大领主莫格莱尼点了点头。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12804, 'zhCN', '你弄到那些肉排了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12805, 'zhCN', '你收回元素身上的能量了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12809, 'zhCN', '嗨，$N!瞧瞧你!离我上一回见你，你成长了一点七三微指距。我很确定外域的空气一定对你有所影响。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12810, 'zhCN', '并不是什么很棒的工作，但却是必须的工作。$B$B现在告诉我，为什么我们要在你完成工作之前一直讲话，$C?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12812, 'zhCN', '$N，欢迎来到奥格瑞玛。大药剂师普崔司在你来以前先捎了口信过来…你手上有那颗受感染的脑子吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12813, 'zhCN', '想必你没那么快完成吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12814, 'zhCN', '为什么花了那么久?他们的狮鹫兽速度很慢吗?我们可得想点办法才行!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12815, 'zhCN', '你这么快回来做什么?我还看到那些突袭军的小子在下面飞来飞去哪!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12816, 'zhCN', '你调查过入侵行动了吗?没有时间可以浪费了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12817, 'zhCN', '你调查过入侵行动了吗?没有时间可以浪费了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12818, 'zhCN', '我们需要一些可以用来加热引擎来对抗这酷寒的东西…快想啊吉尔，快想!$B$B喔嗨!你替我弄到残骸了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12819, 'zhCN', '如果詹克斯在这儿的话，我只要派他去跑这腿就好了。噢…再想一想…我也不太确定我告诉他安全的路径在哪里了。$B$B幸亏有很多你们这种冒险份子在这附近，嗯?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12820, 'zhCN', '我希望你也有顺便宰掉一些恶心的小狗头人，嗯?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12821, 'zhCN', '就算是猛玛象人也该知道不要惹哥布尔。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12822, 'zhCN', '没有什么比哥布尔工兵更能直接对敌人造成恐慌的啦!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12823, 'zhCN', '这个盒装爆裂物可是我的宝贝…花了我好几周的时间才做到完美。好几周!$B$B那可是很长一段时间…好吧。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12826, 'zhCN', '吉尔做好地雷了?好家伙。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12827, 'zhCN', '嘿你又来啦!我肚子在叫…进行的如何?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12828, 'zhCN', '我知道我的计划很棒。别担心啦。$B$B跟大人物相处的感觉怎么样?蛮刺激的对吧?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12829, 'zhCN', '小心那些炸弹配给器!那些东西很危险的。', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12830, 'zhCN', '你替我弄到矿石没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12831, 'zhCN', '<受伤的哥布尔矿工痛苦地扭曲身子。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12832, 'zhCN', '我想洞窟里状况还不错吧?托尔对你好不好?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12833, 'zhCN', '磅!哈哈!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12836, 'zhCN', '我喜欢瑞基特因为她这个人很妙!$B$B噢，你好啊，又见面了!你的害虫控管做的怎么样了?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12838, 'zhCN', '你替我弄到那些数据了吗?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12839, 'zhCN', '血色先锋军情报应该要送去给野牛.厉祸才对。$B$B<统领指挥用一种想打发你走的表情看着你。>', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12840, 'zhCN', '海军上将韦斯温在哪?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12841, 'zhCN', '我的符文呢，$N?你拿到没?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12842, 'zhCN', '不要浪费时间，$N。我们已经濒临天启的时刻了!', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12843, 'zhCN', '你找到我们的男人了吗，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12844, 'zhCN', '有没有找到我们的装备，$N?', 0);
INSERT INTO quest_request_items_locale (ID,locale,CompletionText,VerifiedBuild) VALUES (12847, 'zhCN', '嗯?', 0);
