INSERT INTO `quest_greeting_locale` (`ID`, `Type`, `locale`, `Greeting`, `VerifiedBuild`) VALUES
(241, 0, 'ruRU', 'Послушай $gприятель:приятельница;. Меня зовут Реми.$BЯ с востока Красногорья, а сюда пришел за делом, да, за делом. У тебя не найдется дела? Не найдется?', 12340), -- Replacing an incorrect translation
(253, 0, 'ruRU', 'Ага! Добрый день, добрый день, мастер $c! Пойдем, присядем и выпьем. У тебя предприимчивый взгляд, и я думаю, что ты не зря потратишь время на разговор со мной...', 12340), -- Replacing an incorrect translation
(261, 0, 'ruRU', 'Приветствую, $gгражданин:гражданка;. Ты не выглядишь $gпраздным:праздной; зевакой. Что за дело привело тебя в армию Штормграда?', 12340), -- Replacing an incorrect translation
(264, 0, 'ruRU', 'Если ты просто прогуливаешься, $c, то лучше держаться дороги и делать это только днем. Если дела ждут тебя здесь в Темнолесье, то стоит заглянуть к Ночному Дозору. Наши навыки неоспоримы, но ряды наши малы.', 12340), -- Replacing an incorrect translation
(265, 0, 'ruRU', 'Я знала, что ты придешь, $n. Звезды рассказали мне об этом.', 12340), -- Replacing an incorrect translation
(273, 0, 'ruRU', 'Держи дверь закрытой, $c. Никогда не знаешь, когда Темные всадники пройдут здесь снова.', 12340),
(278, 0, 'ruRU', 'Здравствуйте, $gсэр:леди;. Присаживайтесь и если вы голодны, угощайтесь. Не обращайте внимания на мою занятость, я вас внимательно слушаю...', 12340),
(288, 0, 'ruRU', 'А?!? Ох, вы не похожи на одного из членов Братства Справедливости... или Ночного Дозора. Не пощадите ли бедную душу?', 12340), -- Replacing an incorrect translation
(302, 0, 'ruRU', 'Приветствую, $n.', 12340),
(341, 0, 'ruRU', 'У меня нет времени на разговоры, $n. Мне нужно разобраться с этим мостом до того как пойдет дождь. Я каждый проект заканчивал вовремя, и не собираюсь прокалываться сейчас.', 12340),
(342, 0, 'ruRU', 'Приветствую, $n! Добро пожаловать в мой скромный сад. В эти дни погода прекрасна. Будем надеяться, что она останется такой же до созревания урожая.', 12340),
(381, 0, 'ruRU', 'Приветствую, $c. Если вы здесь по делам, тогда берите себе настойку и обсудим ваши дела.', 12340),
(382, 0, 'ruRU', 'У меня нет времени на болтовню. Но если ты $Gготов:готова; протянуть нам руку помощи в сражении против орков, тогда я найду тебе применение.', 12340), -- Replacing an incorrect translation
(469, 0, 'ruRU', 'Приветствую, $c. Если ты $gодин:одна; из приспешников полковника Курцена, тогда стой где стоишь! Но если ты здесь чтобы помочь нам, тогда я слушаю...', 12340),
(714, 0, 'ruRU', 'Приветствую, $c! Прекрасный день для охоты, не правда ли? У меня не большие проблемы с кабанами. Не желаешь поохотиться?', 12340),
(823, 0, 'ruRU', 'Приветствую тебя, $c. Обычно я присматриваю за народом Штормграда, но в это тяжелое время, многие наши стражники ушли на сражения в другие края. И вот теперь, вместо того чтобы вести патруль, я как заместитель, предлагаю вознаграждение за оказанную помощь...', 12340), -- Replacing an incorrect translation
(1139, 0, 'ruRU', 'Привет. Меня зовут Тупонос, мировой судья Тупонос, если быть точнее. Мне поручено следить за благополучием Телсамара.', 12340),
(1374, 0, 'ruRU', 'Проклятая лига пивоваров! У них есть все лучшие ингредиенты, в то время как мы застряли здесь с зерном и шишками хмеля!', 12340),
(1377, 0, 'ruRU', 'Приветствую, $n.', 12340),
(4794, 0, 'ruRU', 'Заниматься исследованиями в Тераморе - интересная работа, но в этом городе невозможно найти мягкую постель!', 12340),
(5393, 0, 'ruRU', 'Приветствую, $n.', 12340),
(6169, 0, 'ruRU', 'Приветствую, $n.', 12340),
(6579, 0, 'ruRU', 'За Гномреган!', 12340),
(9536, 0, 'ruRU', 'Поиск сокровища – единственная значимая цель для гоблина.$B$BНу, может, сокровища... и большая, громкая смерть!', 12340),
(10260, 0, 'ruRU', 'Добро пожаловать в экзотический питомник Киблера! Чем вы можете мне сегодня помочь?', 12340);
