(17767,0,0,"The Legion's final conquest has begun! Once again the subjugation of this world is within our grasp. Let none survive!",14,0,100,0,0,11022,18221,0,"Rage Winterchill - SAY_INTRO"),
(17767,1,0,"Crumble and rot!",14,0,100,0,0,11023,18222,0,"Rage Winterchill - SAY_DECAY_1"),
(17767,1,1,"Ashes to ashes, dust to dust!",14,0,100,0,0,11055,18223,0,"Rage Winterchill - SAY_DECAY_2"),
(17767,2,0,"Succumb to the icy chill... of death.",14,0,100,0,0,11024,18224,0,"Rage Winterchill - SAY_NOVA_1"),
(17767,2,1,"It will be much colder in your grave.",14,0,100,0,0,11058,18225,0,"Rage Winterchill - SAY_NOVA_2"),
(17767,3,0,"All life must perish!",14,0,100,0,0,11025,18226,0,"Rage Winterchill - SAY_SLAY_1"),
(17767,3,1,"Your world is ours now!",14,0,100,0,0,11056,18227,0,"Rage Winterchill - SAY_SLAY_2"),
(17767,3,2,"Victory to the Legion!",14,0,100,0,0,11057,18228,0,"Rage Winterchill - SAY_SLAY_3"),
(17767,4,0,"You have won this battle, but not... the... war.",14,0,100,0,0,11026,18220,0,"Rage Winterchill - SAY_DEATH"),

(17808,0,0,"You are defenders of a doomed world! Flee here, and perhaps you will prolong your pathetic lives!",14,0,100,0,0,10977,18230,0,"Anetheron - SAY_INTRO"),
(17808,1,0,"You look tired....",14,0,100,0,0,10978,18231,0,"Anetheron - SAY_SLEEP_1"),
(17808,1,1,"Sweet dreams.",14,0,100,0,0,11545,18232,0,"Anetheron - SAY_SLEEP_2"),
(17808,2,0,"The swarm is eager to feed.",14,0,100,0,0,10979,18233,0,"Anetheron - SAY_SWARM_1"),
(17808,2,1,"Pestilence upon you!",14,0,100,0,0,11037,18234,0,"Anetheron - SAY_SWARM_2"),
(17808,3,0,"Let fire rain from above!",14,0,100,0,0,10980,18235,0,"Anetheron - SAY_INFERNO_1"),
(17808,3,1,"Earth and sky shall burn!",14,0,100,0,0,11036,18236,0,"Anetheron - SAY_INFERNO_2"),
(17808,4,0,"Your hopes are lost!",14,0,100,0,0,10981,18237,0,"Anetheron - SAY_SLAY_1"),
(17808,4,1,"Scream for me.",14,0,100,0,0,11038,18238,0,"Anetheron - SAY_SLAY_2"),
(17808,4,2,"Pity, no time for a slow death!",14,0,100,0,0,11039,18239,0,"Anetheron - SAY_SLAY_3"),
(17808,5,0,"The clock... is still... ticking.",14,0,100,0,0,10982,18229,0,"Anetheron - SAY_DEATH"),

(17888,0,0,"Cry for mercy! Your meaningless lives will soon be forfeit!",14,0,100,0,0,11015,18240,0,"Kazrogal - SAY_INTRO"),
(17888,1,0,"Your death will be a painful one.",14,0,100,0,0,11016,18241,0,"Kazrogal - SAY_MARK_1"),
(17888,1,1,"You... are marked.",14,0,100,0,0,11052,18242,0,"Kazrogal - SAY_MARK_2"),
(17888,2,0,"Shaza-kiel!",14,0,100,0,0,11017,18243,0,"Kazrogal - SAY_SLAY_1"),
(17888,2,1,"You are nothing!",14,0,100,0,0,11053,18244,0,"Kazrogal - SAY_SLAY_2"),
(17888,2,2,"Miserable nuisance!",14,0,100,0,0,11054,18245,0,"Kazrogal - SAY_SLAY_3"),

(17842,0,0,"Abandon all hope! The Legion has returned to finish what was begun so many years ago. This time, there will be no escape!",14,0,100,0,0,10999,18247,0,"Azgalor - SAY_INTRO"),
(17842,1,0,"Suffer, you despicable insect!",14,0,100,0,0,11000,18251,0,"Azgalor - SAY_DOOM_1"),
(17842,1,1,"Just a taste... of what awaits you.",14,0,100,0,0,11046,18252,0,"Azgalor - SAY_DOOM_2"),
(17842,2,0,"Reesh, hokta!",14,0,100,0,0,11001,18253,0,"Azgalor - SAY_SLAY_1"),
(17842,2,1,"No one is going to save you.",14,0,100,0,0,11047,18254,0,"Azgalor - SAY_SLAY_2"),
(17842,2,2,"Don't fight it.",14,0,100,0,0,11048,18255,0,"Azgalor - SAY_SLAY_3"),
(17842,3,0,"Your time is almost... up.",14,0,100,0,0,11002,18248,0,"Azgalor - SAY_DEATH");

